import { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { authService } from '../services/api';
import { EditarPerfil } from '../components/EditarPerfil';
import { VisualizarPerfil } from '../components/VisualizarPerfil';
import { AbasAmigos } from '../components/AbasAmigos';
import { Footer } from '../components/Footer';

export function Perfil() {
  const [activeTab, setActiveTab] = useState('ver-perfil');
  const [profileData, setProfileData] = useState(null);
  const { user } = useContext(AuthContext);

  useEffect(() => {
    const loadProfileData = async () => {
      try {
        const data = await authService.getUserProfile();
        setProfileData(data);
      } catch (error) {
        console.error('Erro ao carregar perfil:', error);
      }
    };

    loadProfileData();
  }, []);

  return (
    <div className="flex flex-col min-h-screen">
      <div className="content-section pt-20 animate-fadeIn flex-grow">
        <div className="container mx-auto px-4 pt-24 pb-12">
          <div className="max-w-4xl mx-auto">
            {/* Abas de navegação */}
            <div className="flex border-b border-gray-700 mb-8">
              <button
                className={`px-6 py-3 font-medium ${
                  activeTab === 'ver-perfil'
                    ? 'border-b-2 border-primary text-primary'
                    : 'text-gray-400 hover:text-white'
                }`}
                onClick={() => setActiveTab('ver-perfil')}
              >
                Ver Perfil
              </button>
              <button
                className={`px-6 py-3 font-medium ${
                  activeTab === 'editar-perfil'
                    ? 'border-b-2 border-primary text-primary'
                    : 'text-gray-400 hover:text-white'
                }`}
                onClick={() => setActiveTab('editar-perfil')}
              >
                Editar Perfil
              </button>
              <button
                className={`px-6 py-3 font-medium ${
                  activeTab === 'amigos'
                    ? 'border-b-2 border-primary text-primary'
                    : 'text-gray-400 hover:text-white'
                }`}
                onClick={() => setActiveTab('amigos')}
              >
                Amigos
              </button>
            </div>

            {/* Conteúdo das abas */}
            <div className="mt-6">
              {activeTab === 'ver-perfil' && (
                <VisualizarPerfil usuario={profileData || user} />
              )}
              {activeTab === 'editar-perfil' && (
                <EditarPerfil usuario={profileData || user} />
              )}
              {activeTab === 'amigos' && (
                <AbasAmigos />
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
