import { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';

export function Navbar() {
  const { isAuthenticated, user, dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    dispatch({ type: 'LOGOUT' });
    navigate('/login');
  };

  return (
    <nav className="bg-darker text-light py-4">
      <div className="container mx-auto px-4 flex justify-between items-center">
        <Link to="/" className="text-primary text-2xl font-bold">GeekCore</Link>

        <div className="flex items-center space-x-6">
          {isAuthenticated ? (
            <>
              <Link to="/home" className="hover:text-primary transition-colors">Home</Link>
              <Link to="/favoritos" className="hover:text-primary transition-colors">Meus Favoritos</Link>
              <span className="text-light">Ol√°, {user?.nome}</span>
              <button 
                onClick={handleLogout}
                className="bg-primary text-white px-4 py-2 rounded hover:bg-hover transition-colors"
              >
                Sair
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-primary transition-colors">Login</Link>
              <Link 
                to="/register" 
                className="bg-primary text-white px-4 py-2 rounded hover:bg-hover transition-colors"
              >
                Cadastrar
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
