import { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { contentService, authService } from '../services/api';

export function Detalhes() {
  const { collection, id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    if (id && collection) { // Verifique se ambos estão definidos
      loadDetails();
      loadRecommendations();
    }
  }, [id, collection]);

  async function loadDetails() {
    try {
      setLoading(true);
      setError(null); // Reset error state
      const data = await contentService.getItem(collection, id);
      setItem(data);
    } catch (error) {
      console.error('Erro:', error);
      setError('Não foi possível carregar os detalhes do conteúdo');
    } finally {
      setLoading(false);
    }
  }

  async function loadRecommendations() {
    try {
      if (!collection) return; // Evita chamada com collection undefined
      const recommendations = await contentService.getRecommendations(collection);
      setRecommendations(
        recommendations
          .filter(rec => rec._id !== id)
          .slice(0, 6)
      );
    } catch (error) {
      console.error('Erro ao carregar recomendações:', error);
    }
  }

  const handleFavorite = async () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      await authService.toggleFavorite(id, collection);
      // Adicionar feedback visual
    } catch (error) {
      console.error('Erro ao favoritar:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-darker animate-fadeIn">
      <div className="content-section pt-20"> {/* Adicionado pt-20 para espaçamento do navbar */}
        {error ? (
          <div className="text-center text-red-500 p-4">
            {error}
          </div>
        ) : item ? (
          <div className="relative">
            <div className="absolute inset-0 bg-gradient-to-t from-darker to-transparent"></div>
            <div className="flex flex-col md:flex-row gap-8 relative z-10">
              <div className="w-full md:w-1/3 netflix-card">
                <img 
                  src={item.img_url} 
                  alt={item.titulo}
                  className="w-full rounded-lg shadow-2xl"
                />
              </div>
              
              <div className="flex-1 text-light">
                <div className="flex justify-between items-start">
                  <h1 className="text-4xl md:text-5xl font-bold mb-4 text-primary">{item.titulo}</h1>
                  {isAuthenticated && (
                    <button 
                      onClick={handleFavorite}
                      className="text-4xl hover:text-primary transition-transform hover:scale-110"
                    >
                      ❤️
                    </button>
                  )}
                </div>
                
                {item.categoria && (
                  <div className="mb-6">
                    <span className="bg-primary text-white px-4 py-2 rounded-full text-sm font-bold">
                      {item.categoria}
                    </span>
                  </div>
                )}
                
                {item.descricao && (
                  <p className="text-light text-lg mb-8 leading-relaxed">
                    {item.descricao}
                  </p>
                )}
                
                <a
                  href={item.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-primary text-white px-8 py-4 rounded-lg hover:bg-hover transition-all transform hover:scale-105 inline-flex items-center gap-2 font-bold text-lg"
                >
                  <span>Assistir Agora</span>
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"/>
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z"/>
                  </svg>
                </a>
              </div>
            </div>
          </div>
        ) : null}
        
        {recommendations.length > 0 && (
          <section className="mt-16 mb-8">
            <h2 className="section-title">Recomendados para você</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
              {recommendations.map(rec => (
                <Link 
                  key={rec._id}
                  to={`/detalhes/${collection}/${rec._id}`}
                  className="netflix-card"
                >
                  <div className="aspect-[2/3]">
                    <img 
                      src={rec.img_url} 
                      alt={rec.titulo}
                    />
                    <div className="card-overlay flex items-center justify-center">
                      <span className="netflix-button transform -translate-y-2 group-hover:translate-y-0">
                        Ver Detalhes
                      </span>
                    </div>
                  </div>
                  <h3 className="netflix-title mt-2">{rec.titulo}</h3>
                </Link>
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  );
}
