import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { authService } from '../services/api';

export function Card({ item }) {
  const [isFavorited, setIsFavorited] = useState(item.isFavorited || false);
  const { isAuthenticated } = useContext(AuthContext);
  const userThemeColor = localStorage.getItem('tema_cor') || '#ef4444';

  const handleFavorite = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      const response = await authService.toggleFavorite(item._id, item.collection);
      setIsFavorited(response.isFavorito);
    } catch (error) {
      console.error('Erro ao atualizar favorito:', error);
    }
  };

  return (
    <Link to={`/detalhes/${item.collection}/${item._id}`} className="group relative">
      <div className="relative overflow-hidden rounded-lg aspect-[2/3]">
        <img 
          src={item.img_url || item.img_capa_url} 
          alt={item.titulo}
          className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
        />
        {isAuthenticated && (
          <button
            onClick={handleFavorite}
            className="absolute top-2 right-2 p-2 rounded-full transition-all duration-300"
            style={{
              backgroundColor: isFavorited ? userThemeColor : 'rgba(0, 0, 0, 0.5)',
              border: `2px solid ${userThemeColor}`
            }}
          >
            <svg
              className="w-5 h-5"
              fill={isFavorited ? 'white' : 'none'}
              stroke="white"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
              />
            </svg>
          </button>
        )}
      </div>
      <h3 className="mt-2 text-sm font-medium text-light line-clamp-2">{item.titulo}</h3>
    </Link>
  );
}
