import { memo } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export const Card = memo(({ item, isAuthenticated, onFavorite }) => {
  if (!item) return null;

  const getCollection = (item) => {
    if (item.collection) return item.collection;
    // Verificar o tipo primeiro, depois a categoria
    if (item.tipo === 'anime' || item.tipo === 'animes') return 'animes';
    if (item.categoria?.toLowerCase().includes('anime')) return 'animes';
    return 'filmes';
  };

  const collection = getCollection(item);
  // Sempre normalizar o tipo baseado na collection
  const tipo = collection === 'animes' ? 'anime' : 'filme';

  const handleFavorite = (e) => {
    e.preventDefault();
    e.stopPropagation();
    console.log('Card - Favoritando:', { id: item._id, tipo, collection });
    onFavorite({ ...item, tipo, collection });
  };

  const navigate = useNavigate();

  return (
    <div className="card-container interactive-element relative h-[400px] w-[260px]">
      <Link 
        to={`/detalhes/${item._id}/${collection}`} // Modificado: ID antes da collection
        className="block w-full h-full"
      >
        <div className="relative aspect-[2/3] rounded-lg overflow-hidden">
          <img 
            loading="lazy"
            src={item.img_url} 
            alt={item.titulo}
            className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
          />
          
          <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent 
                        opacity-0 hover:opacity-100 transition-all duration-300">
            <div className="absolute inset-0 flex flex-col items-center justify-center gap-4 
                         opacity-0 hover:opacity-100 transform hover:translate-y-0 
                         transition-all duration-300 translate-y-4">
              <button 
                onClick={(e) => {
                  e.preventDefault();
                  navigate(`/detalhes/${item._id}/${collection}`); // Modificado: ID antes da collection
                }}
                className="button-base rounded-full p-3"
              >
                <span>Assistir Agora</span>
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                        d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"/>
                </svg>
              </button>
            </div>
            
            <div className="absolute bottom-0 w-full p-4">
              <h3 className="text-white font-bold text-lg truncate group-hover:text-primary transition-colors">
                {item.titulo}
              </h3>
            </div>
          </div>
        </div>
      </Link>
      {isAuthenticated && (
        <button
          onClick={handleFavorite}
          className={`favorite-button ${item.isFavorited ? 'favorited' : ''}`}
          aria-label={item.isFavorited ? 'Remover dos favoritos' : 'Adicionar aos favoritos'}
        >
          <svg
            className={`w-6 h-6 ${item.isFavorited ? 'text-primary fill-current' : 'text-white'}`}
            viewBox="0 0 24 24"
            stroke="currentColor"
            fill="none"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
            />
          </svg>
        </button>
      )}
    </div>
  );
});

Card.displayName = 'Card';
