export const mensagemService = {
  async enviarMensagem(destinatarioId, conteudo) {
    const { data } = await api.post('/api/mensagens', {
      destinatario_id: destinatarioId,
      conteudo
    });
    return data;
  },

  async listarConversas() {
    const { data } = await api.get('/api/mensagens/conversas');
    return data;
  },

  async buscarMensagens(userId) {
    const { data } = await api.get(`/api/mensagens/${userId}`);
    return data;
  },

  subscribeToMessages(callback) {
    try {
      const socket = io(process.env.REACT_APP_API_URL || 'http://localhost:3001', {
        auth: { token: localStorage.getItem('token') },
        reconnection: true,
        reconnectionDelay: 1000,
        reconnectionAttempts: 5
      });

      socket.on('connect_error', (error) => {
        console.error('Erro na conexão do WebSocket:', error);
        throw new Error('Erro na conexão do chat');
      });

      socket.on('nova_mensagem', (data) => {
        if (callback) callback(data);
      });

      return () => {
        if (socket) {
          socket.disconnect();
        }
      };
    } catch (error) {
      console.error('Erro ao subscrever mensagens:', error);
      throw error;
    }
  },

  async marcarComoLida(mensagemId) {
    const { data } = await api.put(`/api/mensagens/${mensagemId}/lida`);
    return data;
  }
};

export default api;
