import { useState, useEffect, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { contentService, authService } from '../services/api';
import { Card } from '../components/Card';

export function Home() {
  const [recentItems, setRecentItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const [featuredItem, setFeaturedItem] = useState(null);

  useEffect(() => {
    loadRecentItems();
    loadFeaturedItem();
  }, []);

  async function loadRecentItems() {
    try {
      setLoading(true);
      setError(null);

      const items = await contentService.getRecentContent();
      
      if (!Array.isArray(items)) {
        setRecentItems([]);
        return;
      }

      const processedItems = items.map(item => ({
        ...item,
        tipo: item.tipo || (item.collection === 'animes' ? 'anime' : 'filme'),
        collection: item.collection || (item.tipo === 'anime' ? 'animes' : 'filmes')
      }));

      setRecentItems(processedItems);
    } catch (error) {
      console.error('Erro ao carregar itens:', error);
      setError(error.message || 'Erro ao carregar itens. Tente novamente mais tarde.');
    } finally {
      setLoading(false);
    }
  }

  async function loadFeaturedItem() {
    try {
      const recentItems = await contentService.getRecentContent();
      
      // Filtrar apenas itens que possuem img_capa_url
      const itemsWithCover = recentItems.filter(item => 
        item.img_capa_url && item.img_capa_url.trim() !== ''
      );
      
      if (itemsWithCover.length > 0) {
        const randomIndex = Math.floor(Math.random() * itemsWithCover.length);
        const selectedItem = itemsWithCover[randomIndex];

        // Verificar se o item estÃ¡ nos favoritos
        if (isAuthenticated) {
          const favoritos = await authService.getFavorites();
          const isFavorited = favoritos.some(
            fav => fav.conteudo_id === selectedItem._id || fav._id === selectedItem._id
          );
          selectedItem.isFavorited = isFavorited;
        }

        setFeaturedItem(selectedItem);
      }
    } catch (error) {
      console.error('Erro ao carregar destaque:', error);
    }
  }

  const handleFavorite = async (item) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      const tipo = item.collection === 'animes' ? 'anime' : 'filme';
      const response = await authService.toggleFavorite(item._id, tipo);
      
      // Se o item favoritado for o item em destaque, atualizar seu estado
      if (featuredItem && featuredItem._id === item._id) {
        setFeaturedItem(prev => ({
          ...prev,
          isFavorited: response.isFavorito
        }));
      }

      // Atualizar o item na lista de itens recentes
      setRecentItems(prev => 
        prev.map(recItem => 
          recItem._id === item._id 
            ? { ...recItem }
            : recItem
        )
      );

    } catch (error) {
      console.error('Erro ao favoritar:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="content-section pt-20 animate-fadeIn">
      {error ? (
        <div className="flex flex-col items-center justify-center min-h-[50vh] text-center">
          <p className="text-red-500 mb-4">{error}</p>
          <button 
            onClick={() => {
              setError(null);
              loadRecentItems();
            }}
            className="bg-primary text-white px-4 py-2 rounded hover:bg-hover"
          >
            Tentar Novamente
          </button>
        </div>
      ) : (
        <>
          {/* Hero Section */}
          {featuredItem && featuredItem.img_capa_url && (
            <section className="mb-16">
              <div className="relative h-[70vh] rounded-lg overflow-hidden mb-12">
                <Link 
                  to={`/detalhes/${featuredItem.collection}/${featuredItem._id}`}
                  className="block w-full h-full group"
                >
                  <img 
                    src={featuredItem.img_capa_url}
                    alt={featuredItem.titulo}
                    className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/images/placeholder.jpg';
                    }}
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-darker via-transparent to-transparent">
                    <div className="absolute bottom-0 left-0 p-8">
                      <h1 className="text-5xl font-bold text-white mb-4">{featuredItem.titulo}</h1>
                      <div className="flex items-center space-x-2 nav-button bg-primary text-white px-6 py-3 rounded-full hover:bg-hover transition-all duration-300 w-fit">
                        <span>Assistir Agora</span>
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
                        </svg>
                      </div>
                      {featuredItem.descricao && (
                        <p className="text-white mt-4 max-w-2xl line-clamp-2">
                          {featuredItem.descricao}
                        </p>
                      )}
                    </div>
                  </div>
                </Link>
              </div>
            </section>
          )}

          <section className="mb-16">
            <h2 className="section-title">ConteÃºdo Recente</h2>
            {recentItems.length === 0 ? (
              <p className="text-center text-gray-500">Nenhum item recente encontrado.</p>
            ) : (
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {recentItems.map(item => (
                  <Card 
                    key={item._id} 
                    item={item}
                  />
                ))}
              </div>
            )}
          </section>
        </>
      )}
    </div>
  );
}
