import { useState, useEffect } from 'react';
import { authService } from '../services/api';
import { Card } from '../components/Card';

export function Favoritos() {
  const [favoritos, setFavoritos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadFavoritos = async () => {
      try {
        setLoading(true);
        const data = await authService.getFavorites();
        setFavoritos(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Erro ao carregar favoritos:', error);
        setError('Erro ao carregar seus favoritos. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    loadFavoritos();
  }, []);

  if (loading) {
    return <div className="loading">Carregando...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="content-section pt-20">
      <h1 className="section-title">Meus Favoritos</h1>
      
      {favoritos.length === 0 ? (
        <p className="text-center text-light">Você ainda não tem favoritos.</p>
      ) : (
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {favoritos.map(fav => (
            <Card 
              key={fav.conteudo_id} 
              item={fav.conteudo_id} 
            />
          ))}
        </div>
      )}
    </div>
  );
}
