import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { contentService } from '../services/api';
import { Card } from '../components/Card';
import { Footer } from '../components/Footer';

export function Search() {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const query = searchParams.get('q');
    if (query) {
      searchContent(query);
    }
  }, [searchParams]);

  const searchContent = async (query) => {
    if (!query?.trim()) {
      setResults([]);
      return;
    }

    try {
      setLoading(true);
      setError(null);
      const data = await contentService.searchContent(query);
      setResults(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Erro na busca:', err);
      setError('Não foi possível realizar a busca. Tente novamente mais tarde.');
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <main className="flex-grow content-section pt-20">
        <h1 className="text-3xl font-bold mb-8">
          Resultados para: {searchParams.get('q')}
        </h1>

        {loading ? (
          <div className="flex items-center justify-center py-20">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
          </div>
        ) : error ? (
          <div className="text-red-500 text-center py-8">
            <p>{error}</p>
            <button 
              onClick={() => searchContent(searchParams.get('q'))}
              className="mt-4 px-4 py-2 bg-primary text-white rounded hover:bg-hover"
            >
              Tentar Novamente
            </button>
          </div>
        ) : results.length === 0 ? (
          <div className="text-center py-8 text-gray-400">
            Nenhum resultado encontrado
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {results.map(item => (
              <Card key={item._id} item={item} />
            ))}
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}
