import { useEffect, useState } from 'react';

export function Alert({ message, type = 'info', duration = 3000, onClose }) {
  const [isVisible, setIsVisible] = useState(true);

  // Definir cores baseadas no tipo
  const alertStyles = {
    success: 'bg-green-500',
    error: 'bg-red-500',
    info: 'bg-blue-500',
    warning: 'bg-yellow-500'
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(false);
      onClose?.();
    }, duration);

    return () => clearTimeout(timer);
  }, [duration, onClose]);

  if (!isVisible) return null;

  return (
    <div
      className={`
        fixed top-4 left-1/2 transform -translate-x-1/2 px-6 py-3 rounded-lg shadow-lg z-50
        ${alertStyles[type]} text-white font-medium flex items-center gap-2
        animate-fadeIn
      `}
    >
      {type === 'info' && <span className="text-xl">ℹ️</span>}
      {type === 'warning' && <span className="text-xl">⚠️</span>}
      {type === 'success' && <span className="text-xl">✅</span>}
      {type === 'error' && <span className="text-xl">❌</span>}
      
      <span className="font-semibold">{message}</span>
    </div>
  );
}
