import { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { contentService, authService } from '../services/api';
import { Carousel } from '../components/Carousel';
import { Alert } from '../components/Alert';

export function Detalhes() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState([]);
  const [toast, setToast] = useState({ show: false, message: '', type: '' });

  useEffect(() => {
    const loadContent = async () => {
      try {
        setLoading(true);
        setError(null);

        const itemData = await contentService.getItem(id);
        const favoritesData = await authService.getFavorites();

        // Garantir que temos a collection
        if (!itemData.collection) {
          itemData.collection = window.location.pathname.split('/')[2]; // Pegar da URL
        }

        const isFavorited = favoritesData.some(fav => fav._id === id);
        setItem({ ...itemData, isFavorited });

        const recommendationsData = await contentService.getRecommendations();
        setRecommendations(recommendationsData.filter(rec => rec._id !== id).slice(0, 6));
      } catch (error) {
        console.error('Erro ao carregar conteúdo:', error);
        setError('Erro ao carregar conteúdo. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      loadContent();
    }
  }, [id]);

  const handleFavorite = async () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      // Extrair o tipo da URL
      const tipo = window.location.pathname.split('/')[2];
      
      if (!id || !tipo) {
        throw new Error('Informações necessárias não encontradas');
      }

      const response = await authService.toggleFavorite(id, tipo);
      
      setItem(prev => ({
        ...prev,
        isFavorited: response.isFavorito
      }));

      setAlert({
        show: true,
        message: response.message,
        type: response.isFavorito ? 'success' : 'info'
      });

      setTimeout(() => setAlert({ show: false, message: '', type: '' }), 3000);
    } catch (error) {
      console.error('Erro ao atualizar favoritos:', error);
      setAlert({
        show: true,
        message: error.message || 'Erro ao atualizar favoritos',
        type: 'error'
      });
    }
  };

  const FavoriteButton = () => (
    <button
      onClick={handleFavorite}
      className="group flex flex-col items-center transition-all duration-300"
      title={item.isFavorited ? 'Remover dos favoritos' : 'Adicionar aos favoritos'}
    >
      <div className={`p-3 rounded-full border-2 transition-all duration-300 ${
        item.isFavorited
          ? 'bg-[var(--color-primary)] border-[var(--color-primary)] hover:bg-[var(--color-hover)] hover:border-[var(--color-hover)]'
          : 'bg-transparent border-[var(--color-primary)] hover:bg-[var(--color-primary)]'
      }`}>
        <svg
          className={`w-6 h-6 ${item.isFavorited ? 'text-white' : 'text-[var(--color-primary)]'} group-hover:text-white transition-colors`}
          fill={item.isFavorited ? 'currentColor' : 'none'}
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
          />
        </svg>
      </div>
      <span className="text-sm mt-1 text-gray-300">
        {item.isFavorited ? 'Favoritado' : 'Favoritar'}
      </span>
    </button>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-darker animate-fadeIn relative">
      {alert.show && (
        <Alert
          message={alert.message}
          type={alert.type}
          onClose={() => setAlert({ show: false, message: '', type: '' })}
        />
      )}
      {toast.show && (
        <Alert
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ show: false, message: '', type: '' })}
        />
      )}

      <div className="content-section pt-20">
        {error ? (
          <div className="text-center text-red-500 p-4">{error}</div>
        ) : item ? (
          <>
            <div className="flex flex-col md:flex-row gap-8">
              <div className="w-full md:w-1/3 netflix-card">
                <img 
                  src={item.img_url || item.img_capa_url} 
                  alt={item.titulo}
                  className="w-full rounded-lg shadow-2xl"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/public/images/default-poster.jpg';
                  }}
                />
              </div>
              
              <div className="flex-1">
                <h1 className="text-4xl font-bold mb-4 text-light">{item.titulo}</h1>
                {item.categoria && (
                  <span className="bg-[var(--color-primary)] text-white px-4 py-2 rounded-full text-sm font-bold mb-4 inline-block">
                    {item.categoria}
                  </span>
                )}
                {item.descricao && (
                  <p className="text-light text-lg mb-8 mt-4">{item.descricao}</p>
                )}
                
                <div className="flex items-center gap-4 mb-6">
                  <a
                    href={item.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-[var(--color-primary)] hover:bg-[var(--color-hover)] text-white px-6 py-3 rounded transition-all inline-flex items-center gap-2"
                  >
                    <span>Assistir Agora</span>
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"/>
                    </svg>
                  </a>
                </div>

                <div className="flex items-center gap-6 mt-4">
                  {isAuthenticated && <FavoriteButton />}
                </div>
              </div>
            </div>

            {recommendations.length > 0 && (
              <div className="mt-12">
                <Carousel items={recommendations} title="Recomendações" />
              </div>
            )}
          </>
        ) : null}
      </div>
    </div>
  );
}
