import { useState, useEffect, useContext } from 'react';
import { authService } from '../services/api';
import { AuthContext } from '../context/AuthContext';

export function Perfil() {
  const [userData, setUserData] = useState({ nome: '', email: '', descricao: '', foto: '' });
  const [senhas, setSenhas] = useState({ atual: '', nova: '' });
  const [showPasswords, setShowPasswords] = useState({ atual: false, nova: false });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previewFoto, setPreviewFoto] = useState('');
  const { updateUserData } = useContext(AuthContext);

  useEffect(() => {
    async function fetchUserData() {
      try {
        setLoading(true);
        const data = await authService.getUserProfile();
        setUserData(data);
        setPreviewFoto(data.foto);
      } catch (err) {
        setError('Erro ao carregar dados do perfil.');
      } finally {
        setLoading(false);
      }
    }
    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleFotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) { // 5MB
        setError('Arquivo muito grande. MÃ¡ximo: 5MB');
        return;
      }

      const reader = new FileReader();
      reader.onload = () => {
        setPreviewFoto(reader.result);
        setUserData(prev => ({ ...prev, foto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      setError(null);

      const updateData = {
        ...userData,
        senha_atual: senhas.atual,
        nova_senha: senhas.nova
      };

      const response = await authService.updateUserProfile(updateData);
      
      // Atualizar o contexto com os novos dados
      updateUserData({
        nome: userData.nome,
        foto: userData.foto,
        descricao: userData.descricao
      });

      alert('Perfil atualizado com sucesso!');
      setSenhas({ atual: '', nova: '' });
      
    } catch (err) {
      setError(err.response?.data?.message || 'Erro ao atualizar perfil.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Carregando...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="container mx-auto px-4 py-8 bg-darker text-white rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold mb-6 text-center">Editar Perfil</h1>
      <div className="flex flex-col items-center gap-6">
        <div className="relative">
          <img
            src={previewFoto || '/images/default-avatar.png'}
            alt="Foto de Perfil"
            className="w-32 h-32 rounded-full object-cover border-4 border-primary"
          />
          <label
            htmlFor="foto"
            className="absolute bottom-0 right-0 bg-primary text-white p-2 rounded-full cursor-pointer hover:bg-hover transition"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 12h14M12 5l7 7-7 7" />
            </svg>
          </label>
          <input
            type="file"
            id="foto"
            accept="image/*"
            className="hidden"
            onChange={handleFotoChange}
          />
        </div>
        <div className="w-full max-w-md">
          <label className="block mb-2 font-semibold">Nome:</label>
          <input
            type="text"
            name="nome"
            value={userData.nome}
            onChange={handleInputChange}
            className="w-full p-3 rounded bg-dark text-white border border-gray-700 focus:outline-none focus:border-primary"
          />
        </div>
        <div className="w-full max-w-md">
          <label className="block mb-2 font-semibold">Email:</label>
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleInputChange}
            className="w-full p-3 rounded bg-dark text-white border border-gray-700 focus:outline-none focus:border-primary"
            disabled
          />
        </div>
        <div className="w-full max-w-md">
          <label className="block mb-2 font-semibold">Senha Atual:</label>
          <div className="relative">
            <input
              type={showPasswords.atual ? 'text' : 'password'}
              value={senhas.atual}
              onChange={(e) => setSenhas(prev => ({ ...prev, atual: e.target.value }))}
              className="w-full p-3 rounded bg-dark text-white border border-gray-700 focus:outline-none focus:border-primary"
            />
            <button
              type="button"
              onClick={() => setShowPasswords(prev => ({ ...prev, atual: !prev.atual }))}
              className="absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400 hover:text-white"
            >
              {showPasswords.atual ? 'Ocultar' : 'Exibir'}
            </button>
          </div>
        </div>
        <div className="w-full max-w-md">
          <label className="block mb-2 font-semibold">Nova Senha:</label>
          <div className="relative">
            <input
              type={showPasswords.nova ? 'text' : 'password'}
              value={senhas.nova}
              onChange={(e) => setSenhas(prev => ({ ...prev, nova: e.target.value }))}
              className="w-full p-3 rounded bg-dark text-white border border-gray-700 focus:outline-none focus:border-primary"
            />
            <button
              type="button"
              onClick={() => setShowPasswords(prev => ({ ...prev, nova: !prev.nova }))}
              className="absolute top-1/2 right-3 transform -translate-y-1/2 text-gray-400 hover:text-white"
            >
              {showPasswords.nova ? 'Ocultar' : 'Exibir'}
            </button>
          </div>
        </div>
        <div className="w-full max-w-md">
          <label className="block mb-2 font-semibold">DescriÃ§Ã£o:</label>
          <textarea
            name="descricao"
            value={userData.descricao}
            onChange={handleInputChange}
            className="w-full p-3 rounded bg-dark text-white border border-gray-700 focus:outline-none focus:border-primary"
          />
        </div>
        <button
          onClick={handleSave}
          className="bg-primary text-white px-6 py-3 rounded hover:bg-hover transition-colors w-full max-w-md"
        >
          Salvar AlteraÃ§Ãµes
        </button>
      </div>
    </div>
  );
}
