import { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { authService } from '../services/api';

export function Login() {
  const [formData, setFormData] = useState({ email: '', senha: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  function handleChange(e) {
    const { name, value } = e.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const { user, token, success } = await authService.login(formData.email, formData.senha);
      
      if (success && token) {
        dispatch({ type: 'LOGIN', payload: { user, token } });
        navigate('/');
      } else {
        setError('Erro ao fazer login. Tente novamente.');
      }
    } catch (err) {
      setError(err.message || 'Email ou senha inválidos');
      console.error('Erro de login:', err);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-darker">
      <div className="bg-dark p-8 rounded-lg shadow-xl w-96">
        <h1 className="text-2xl font-bold text-light mb-6">Login</h1>
        
        {error && (
          <div className="bg-red-500 text-white p-3 rounded mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              className="w-full p-3 rounded bg-darker text-light"
              required
            />
          </div>

          <div className="mb-6">
            <input
              type="password"
              name="senha"
              placeholder="Senha"
              value={formData.senha}
              onChange={handleChange}
              className="w-full p-3 rounded bg-darker text-light"
              required
              minLength={6}
              autoComplete="current-password"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary text-white p-3 rounded hover:bg-hover transition-colors disabled:opacity-50"
          >
            {loading ? 'Carregando...' : 'Entrar'}
          </button>
        </form>

        <p className="text-light text-center mt-4">
          Não tem uma conta?{' '}
          <Link to="/register" className="text-primary hover:text-hover">
            Cadastre-se
          </Link>
        </p>
      </div>
    </div>
  );
}
