import { useState, useContext, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { authService } from '../services/api';

export function Login() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      console.log('Token recebido do Google:', token); // Log para depuração
      dispatch({ type: 'LOGIN', payload: { token } });
      navigate('/');
    }
  }, [dispatch, navigate]);

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const { user, token, success } = await authService.login(email, senha);
      
      if (success && token) {
        dispatch({ type: 'LOGIN', payload: { user, token } });
        navigate('/');
      } else {
        setError('Erro ao fazer login. Tente novamente.');
      }
    } catch (err) {
      setError(err.message || 'Email ou senha inválidos');
      console.error('Erro de login:', err);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-darker">
      <div className="bg-dark p-8 rounded-lg shadow-xl w-96">
        <h1 className="text-2xl font-bold text-light mb-6">Entrar</h1>
        
        {error && (
          <div className="bg-red-500 text-white p-3 rounded mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="mb-4">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              className="w-full p-3 rounded bg-darker text-light"
              required
            />
          </div>

          <div className="mb-6">
            <input
              type="password"
              placeholder="Senha"
              value={senha}
              onChange={e => setSenha(e.target.value)}
              className="w-full p-3 rounded bg-darker text-light"
              required
              minLength={6}
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary text-white p-3 rounded hover:bg-hover transition-colors disabled:opacity-50"
          >
            {loading ? 'Entrando...' : 'Entrar'}
          </button>
        </form>

        <div className="flex flex-col items-center mt-4 space-y-2">
          <a href="/auth/google" className="w-full bg-red-500 text-white p-3 rounded hover:bg-red-600 transition-colors">
            Entrar com Google
          </a>
        </div>

        <p className="text-light text-center mt-4">
          Não tem uma conta?{' '}
          <Link to="/register" className="text-primary hover:text-hover">
            Cadastrar
          </Link>
        </p>
      </div>
    </div>
  );
}
