import React from 'react';

export function VisualizarPerfil({ usuario }) {
  return (
    <div className="bg-darker rounded-lg p-6 max-w-2xl mx-auto">
      <div className="flex flex-col items-center space-y-4">
        <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-primary">
          <img
            src={usuario.foto || '/images/default-avatar.png'}
            alt={usuario.nome}
            className="w-full h-full object-cover"
          />
        </div>
        
        <h1 className="text-2xl font-bold text-white">{usuario.nome}</h1>
        
        {usuario.descricao && (
          <p className="text-gray-300 text-center">{usuario.descricao}</p>
        )}

        <div className="flex items-center space-x-4 text-gray-400">
          <span>Membro desde: {new Date(usuario.data_criacao).toLocaleDateString()}</span>
          <span>â€¢</span>
          <span>{usuario.amigos?.length || 0} amigos</span>
        </div>
      </div>
    </div>
  );
}
