import { createContext, useContext, useEffect, useState } from 'react';

export const ThemeContext = createContext();

export function ThemeProvider({ children }) {
  const [themeColor, setThemeColor] = useState(() => 
    localStorage.getItem('tema_cor') || '#ef4444'
  );

  const isValidHexColor = (color) => {
    return /^#([A-Fa-f0-9]{6})$/.test(color);
  };

  const updateThemeColor = (color) => {
    if (!color || !isValidHexColor(color)) {
      console.error('Cor inválida:', color);
      color = '#ef4444'; // cor padrão se inválida
    }

    try {
      document.documentElement.style.setProperty('--color-primary', color);
      document.documentElement.style.setProperty(
        '--color-hover',
        `color-mix(in srgb, ${color} 80%, black)`
      );
      localStorage.setItem('tema_cor', color);
      setThemeColor(color);
    } catch (error) {
      console.error('Erro ao atualizar cor do tema:', error);
    }
  };

  useEffect(() => {
    const savedColor = localStorage.getItem('tema_cor');
    if (savedColor && isValidHexColor(savedColor)) {
      updateThemeColor(savedColor);
    }
  }, []);

  return (
    <ThemeContext.Provider value={{ themeColor, updateThemeColor }}>
      {children}
    </ThemeContext.Provider>
  );
}

export const useTheme = () => useContext(ThemeContext);
