import { useState, useEffect, useContext } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import api, { authService } from '../services/api';

export function Search() {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const query = searchParams.get('q');

  useEffect(() => {
    if (query) {
      searchContent();
    }
  }, [query]);

  async function searchContent() {
    try {
      setLoading(true);
      const response = await api.get(`/api/search?q=${encodeURIComponent(query)}`);
      setResults(response.data);
    } catch (error) {
      console.error('Erro na busca:', error);
    } finally {
      setLoading(false);
    }
  }

  const handleFavorite = async (item) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    try {
      await authService.toggleFavorite(item._id, item.collection);
    } catch (error) {
      console.error('Erro ao favoritar:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 pt-20">
      <h1 className="text-3xl font-bold text-light mb-8">
        Resultados para: {query}
      </h1>
      
      {results.length === 0 ? (
        <p className="text-light text-center">Nenhum resultado encontrado.</p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {results.map(item => (
            <div key={item._id} className="relative group">
              <img 
                src={item.img_url} 
                alt={item.titulo}
                className="w-full h-64 object-cover rounded-lg"
              />
              <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                <button 
                  onClick={() => window.location.href = item.url}
                  className="bg-primary text-white px-4 py-2 rounded hover:bg-hover transition-colors mb-2"
                >
                  Assistir
                </button>
                {isAuthenticated && (
                  <button 
                    onClick={() => handleFavorite(item)}
                    className="text-white hover:text-primary transition-colors"
                  >
                    ❤️
                  </button>
                )}
              </div>
              <h3 className="text-light text-center mt-2">{item.titulo}</h3>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
