import axios from 'axios';
import imageCompression from 'browser-image-compression';

const getBaseUrl = () => {
  const hostname = window.location.hostname;
  return process.env.REACT_APP_API_URL || `http://${hostname}:3001`;
};

const api = axios.create({
  baseURL: getBaseUrl(),
  timeout: 60000, // Aumentar timeout para 60 segundos
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  withCredentials: true,
  maxContentLength: 50 * 1024 * 1024, // 50MB
  maxBodyLength: 50 * 1024 * 1024 // 50MB
});

// Adicionar interceptador para compressão de imagens
api.interceptors.request.use(async config => {
  if (config.data && (config.data.foto_capa || config.data.foto)) {
    const compressImage = async (base64Str, maxSizeMB) => {
      if (!base64Str || !base64Str.startsWith('data:image')) return base64Str;
      
      try {
        // Converter base64 para Blob
        const fetchResponse = await fetch(base64Str);
        const blob = await fetchResponse.blob();
        
        const options = {
          maxSizeMB: maxSizeMB,
          maxWidthOrHeight: 2048,
          useWebWorker: true,
          onProgress: (progress) => console.log('Compressão:', Math.round(progress * 100), '%')
        };
        
        const compressedBlob = await imageCompression(blob, options);
        return new Promise((resolve) => {
          const reader = new FileReader();
          reader.onloadend = () => resolve(reader.result);
          reader.readAsDataURL(compressedBlob);
        });
      } catch (err) {
        console.error('Erro ao comprimir imagem:', err);
        return base64Str;
      }
    };

    if (config.data.foto) {
      config.data.foto = await compressImage(config.data.foto, 2);
    }
    if (config.data.foto_capa) {
      config.data.foto_capa = await compressImage(config.data.foto_capa, 10);
    }
  }
  
  return config;
});

// Adicionar manipulador de progresso para uploads grandes
api.interceptors.request.use(config => {
  if (config.data && (config.data.foto_capa || config.data.foto)) {
    config.onUploadProgress = progressEvent => {
      const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
      console.log(`Upload progress: ${percentCompleted}%`);
    };
  }
  return config;
});

// Adicionar interceptor de retry
api.interceptors.response.use(null, async (error) => {
  const { config } = error;
  if (!config || !config.retry) {
    return Promise.reject(error);
  }
  
  config.retryCount = config.retryCount || 0;
  
  if (config.retryCount >= config.retry) {
    return Promise.reject(error);
  }
  
  config.retryCount += 1;
  await new Promise(resolve => setTimeout(resolve, config.retryDelay(config.retryCount)));
  return api(config);
});

api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Melhorar o interceptor de erro
api.interceptors.response.use(
  response => response,
  async error => {
    if (!error.response) {
      console.error('Erro de rede:', error.message);
      throw new Error('Servidor indisponível. Verifique sua conexão.');
    }

    if (error.response.status === 503) {
      console.error('Servidor em manutenção');
      throw new Error('Servidor em manutenção. Tente novamente em alguns instantes.');
    }

    if (error.code === 'ERR_NETWORK') {
      console.error('Erro de conexão - Servidor está rodando?');
      // Tentar reconectar
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve(api(error.config));
        }, 3000);
      });
    }
    
    console.error('API Error:', {
      status: error.response?.status,
      message: error.response?.data?.message || error.message,
      url: error.config?.url
    });
    
    return Promise.reject(error);
  }
);

const compressImage = async (base64Image, maxSizeMB) => {
  if (!base64Image || !base64Image.startsWith('data:image')) {
    return base64Image;
  }

  try {
    // Converter base64 para Blob
    const response = await fetch(base64Image);
    const blob = await response.blob();
    
    // Configurações de compressão
    const options = {
      maxSizeMB: maxSizeMB,
      maxWidthOrHeight: 2048,
      useWebWorker: true,
      onProgress: percent => console.log('Compressão:', Math.round(percent * 100), '%')
    };

    // Comprimir imagem
    const compressedBlob = await imageCompression(blob, options);
    
    // Converter blob comprimido para base64
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(compressedBlob);
    });
  } catch (err) {
    console.error('Erro ao comprimir imagem:', err);
    return base64Image;
  }
};

export const authService = {
  async login(email, senha) {
    try {
      console.log('Tentando login com:', { email }); // Log para debug

      const { data } = await api.post('/api/auth/login', { 
        email: email.toLowerCase().trim(),
        senha: senha
      });

      console.log('Resposta do servidor:', data); // Log para debug

      if (data.success && data.token) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        return data;
      }
      
      throw new Error(data.message || 'Erro ao fazer login');
    } catch (error) {
      console.error('Erro de login:', error.response?.data || error);
      throw new Error(error.response?.data?.message || 'Erro ao fazer login');
    }
  },

  async register(nome, email, senha) {
    const { data } = await api.post('/api/auth/register', { nome, email, senha });
    return data;
  },

  async toggleFavorite(conteudoId, tipo) {
    try {
      // Garantir tipo normalizado
      const normalizedTipo = tipo.toLowerCase().replace(/s$/, '');
      
      console.log('Enviando requisição de favorito:', {
        conteudo_id: conteudoId,
        tipo: normalizedTipo
      });

      const response = await api.post('/api/auth/favoritos', {
        conteudo_id: conteudoId,
        tipo: normalizedTipo
      });

      console.log('Resposta do servidor:', response.data);

      return {
        success: true,
        isFavorito: response.data.isFavorito,
        message: response.data.message
      };
    } catch (error) {
      console.error('Erro ao favoritar:', error.response?.data || error.message);
      throw error;
    }
  },

  async getFavorites() {
    try {
      const token = localStorage.getItem('token');
      if (!token) return [];

      const { data } = await api.get('/api/auth/favoritos');
      
      return Array.isArray(data) ? data.map(item => ({
        ...item,
        tipo: item.tipo || (item.collection === 'animes' ? 'anime' : 'filme'),
        collection: item.collection || (item.tipo === 'anime' ? 'animes' : 'filmes'),
        isFavorito: true,
        isFavorited: true
      })) : [];
    } catch (error) {
      console.error('Erro ao buscar favoritos:', error);
      return [];
    }
  },

  async getUserProfile() {
    const { data } = await api.get('/api/auth/perfil');
    return data;
  },

  async updateUserProfile(userData) {
    try {
      // Comprimir imagens antes do envio
      const compressedData = {
        ...userData,
        foto: userData.foto ? await compressImage(userData.foto, 2) : userData.foto,
        foto_capa: userData.foto_capa ? await compressImage(userData.foto_capa, 10) : userData.foto_capa
      };

      const response = await api.put('/api/auth/perfil', compressedData);
      
      if (response.data.user) {
        const updatedUser = {
          ...JSON.parse(localStorage.getItem('user') || '{}'),
          ...response.data.user
        };
        localStorage.setItem('user', JSON.stringify(updatedUser));
      }
      
      return response.data;
    } catch (error) {
      console.error('Erro ao atualizar perfil:', error);
      if (error.response?.status === 413) {
        throw new Error('Imagem muito grande. Por favor, use uma imagem menor.');
      }
      throw error;
    }
  },

  async changePassword(senhaAtual, novaSenha) {
    try {
      const { data } = await api.post('/api/auth/change-password', {
        senha_atual: senhaAtual,
        nova_senha: novaSenha
      });
      return data;
    } catch (error) {
      throw error;
    }
  }
};

