              tipo: data.collection === 'animes' ? 'anime' : 'filme'
            };
            break;
          }
        } catch (err) {
          error = err;
        }
      }

      if (!item) {
        throw error || new Error('Item não encontrado');
      }

      return item;
    } catch (error) {
      console.error('Erro ao buscar item:', error.message);
      throw error;
    }
  },

  async getItemFromCollection(collection, id) {
    try {
      const { data } = await api.get(`/api/item/${collection}/${id}`);
      if (!data) return null;

      // Garantir que os campos collection e tipo estejam corretos
      const normalizedCollection = data.collection || collection;
      const normalizedTipo = normalizedCollection === 'animes' ? 'anime' : 'filme';

      return {
        ...data,
        collection: normalizedCollection,
        tipo: normalizedTipo
      };
    } catch (error) {
      console.error(`Erro ao buscar item na coleção ${collection}:`, error);
      return null;
    }
  },

  async getRecommendations(collection) {
    try {
      // Buscar recomendações da mesma coleção e da outra coleção
      const otherCollection = collection === 'animes' ? 'filmes' : 'animes';
      
      const [sameCollectionData, otherCollectionData] = await Promise.all([
        api.get(`/api/random/${collection}?limit=6`),
        api.get(`/api/random/${otherCollection}?limit=6`)
      ]);

      let recommendations = [
        ...(sameCollectionData.data || []).map(item => ({
          ...item,
          collection: collection,
          tipo: collection === 'animes' ? 'anime' : 'filme'
        })),
        ...(otherCollectionData.data || []).map(item => ({
          ...item,
          collection: otherCollection,
          tipo: otherCollection === 'animes' ? 'anime' : 'filme'
        }))
      ];

      // Buscar favoritos se o usuário estiver autenticado
      let favoritos = [];
      const token = localStorage.getItem('token');
      if (token) {
        favoritos = await authService.getFavorites();
      }

      // Adicionar estado de favorito
      recommendations = recommendations.map(item => ({
        ...item,
        isFavorited: favoritos.some(fav => 
          fav.conteudo_id === item._id || fav._id === item._id
        )
      }));

      return recommendations;
    } catch (error) {
      console.error('Erro ao buscar recomendações:', error.message);
      return [];
    }
  }
};

export default api;
