import React, { useState, useEffect } from 'react';
import { mensagemService } from '../services/api';
import { useAlert } from '../contexts/AlertContext';

export function ListaConversas({ onSelectConversation }) {
  const [conversas, setConversas] = useState([]);
  const [loading, setLoading] = useState(true);
  const { showAlert } = useAlert();

  useEffect(() => {
    carregarConversas();
  }, []);

  const carregarConversas = async () => {
    try {
      setLoading(true);
      const data = await mensagemService.listarConversas();
      setConversas(data);
    } catch (error) {
      showAlert('Erro ao carregar conversas', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-darker rounded-lg w-80 flex flex-col h-full">
      <div className="p-4 bg-dark border-b border-gray-700">
        <h2 className="text-xl font-semibold">Conversas</h2>
      </div>

      <div className="flex-1 overflow-y-auto">
        {loading ? (
          <div className="flex justify-center p-4">
            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-primary"></div>
          </div>
        ) : conversas.length > 0 ? (
          conversas.map((conversa) => (
            <button
              key={conversa._id}
              onClick={() => onSelectConversation(conversa)}
              className="w-full p-4 hover:bg-dark flex items-center space-x-3 border-b border-gray-700 transition-colors"
            >
              <img
                src={conversa.foto || '/images/default-avatar.png'}
                alt={conversa.nome}
                className="w-12 h-12 rounded-full object-cover"
              />
              <div className="flex-1 text-left">
                <h3 className="font-medium">{conversa.nome}</h3>
                <p className="text-sm text-gray-400 truncate">
                  {conversa.ultima_mensagem}
                </p>
              </div>
              {conversa.nao_lidas > 0 && (
                <span className="bg-primary text-white text-xs rounded-full px-2 py-1">
                  {conversa.nao_lidas}
                </span>
              )}
            </button>
          ))
        ) : (
          <div className="text-center p-4 text-gray-400">
            Nenhuma conversa encontrada
          </div>
        )}
      </div>
    </div>
  );
}
