import React, { useState } from 'react';
import { ratingService } from '../services/api';

export function RatingForm({ itemId, onRatingSubmit }) {
  const [rating, setRating] = useState(0);
  const [review, setReview] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (rating < 1) {
      alert('Por favor, selecione uma avaliação');
      return;
    }

    try {
      setSubmitting(true);
      const data = await ratingService.submitRating(itemId, rating, review);
      onRatingSubmit?.(data);
      setReview('');
      setRating(0);
    } catch (error) {
      alert('Erro ao enviar avaliação');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="flex items-center space-x-2">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            onClick={() => setRating(star)}
            className={`text-2xl ${rating >= star ? 'text-yellow-400' : 'text-gray-400'}`}
          >
            ★
          </button>
        ))}
      </div>
      
      <textarea
        value={review}
        onChange={(e) => setReview(e.target.value)}
        placeholder="Digite sua avaliação (opcional)"
        className="w-full p-3 rounded bg-dark text-light"
        maxLength={1000}
      />
      
      <button
        type="submit"
        disabled={submitting || rating < 1}
        className="bg-primary text-white px-4 py-2 rounded disabled:opacity-50 hover:bg-hover"
      >
        {submitting ? 'Enviando...' : 'Enviar Avaliação'}
      </button>
    </form>
  );
}
