import React from 'react';
import { Link } from 'react-router-dom';

export function Card({ item, isAuthenticated, onFavorite }) {
  const handleFavoriteClick = (e) => {
    e.preventDefault();
    onFavorite(item);
  };

  // Obter a cor do tema do usuário do localStorage
  const userThemeColor = localStorage.getItem('tema_cor') || '#ef4444';

  return (
    <div className="relative group">
      <Link to={`/detalhes/${item.collection}/${item._id}`} className="block">
        <img
          src={item.img_url || '/images/placeholder.jpg'}
          alt={item.titulo}
          className="w-full h-full object-cover rounded-lg"
        />
        {isAuthenticated && (
          <button
            onClick={handleFavoriteClick}
            style={{
              '--user-theme-color': userThemeColor,
              '--user-theme-hover': userThemeColor + 'dd'
            }}
            className={`absolute top-2 right-2 p-3 rounded-full border-2 transition-all duration-300 
              ${item.isFavorited 
                ? 'bg-[var(--user-theme-color)] text-white border-[var(--user-theme-color)] hover:bg-[var(--user-theme-hover)] hover:border-[var(--user-theme-hover)]' 
                : 'bg-transparent border-[var(--user-theme-color)] text-[var(--user-theme-color)] hover:bg-[var(--user-theme-color)] hover:text-white'
              }`}
          >
            <svg
              className={`w-6 h-6 transition-transform duration-300 ${
                item.isFavorited ? 'heart-beat' : ''
              }`}
              fill={item.isFavorited ? 'currentColor' : 'none'}
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
              />
            </svg>
          </button>
        )}
      </Link>
    </div>
  );
}
