import { useState, useRef, useEffect } from 'react';
import { Card } from './Card';

export const Carousel = ({ items, title }) => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [maxScroll, setMaxScroll] = useState(0);
  const trackRef = useRef(null);

  useEffect(() => {
    if (trackRef.current) {
      setMaxScroll(trackRef.current.scrollWidth - trackRef.current.clientWidth);
    }
  }, [items]);

  const scroll = (direction) => {
    const scrollAmount = direction === 'left' ? -800 : 800;
    const newPosition = Math.max(0, Math.min(scrollPosition + scrollAmount, maxScroll));
    setScrollPosition(newPosition);
    trackRef.current.style.transform = `translateX(-${newPosition}px)`;
  };

  if (!items?.length) return null;

  return (
    <section className="mt-16 relative">
      <h2 className="text-2xl font-bold mb-6 text-light pl-4 border-l-4 border-primary">
        {title || 'Sugestões'}
      </h2>
      
      <div className="relative group">
        <button 
          onClick={() => scroll('left')}
          className="carousel-nav-btn left-0"
          disabled={scrollPosition === 0}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        <div className="overflow-hidden mx-4">
          <div 
            ref={trackRef}
            className="flex gap-4 transition-transform duration-500 ease-out"
          >
            {items.map((item, index) => (
              <div key={item._id || index} className="flex-none w-[200px] sm:w-[250px]">
                <Card item={item} />
              </div>
            ))}
          </div>
        </div>

        <button 
          onClick={() => scroll('right')}
          className="carousel-nav-btn right-0"
          disabled={scrollPosition >= maxScroll}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>
      </div>
    </section>
  );
};
