import React, { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { authService, amigoService } from '../services/api';
import { AuthContext } from '../contexts/AuthContext';

export function VisualizarPerfil({ usuario }) {
  const FALLBACK_AVATAR = 'https://via.placeholder.com/200x200?text=Avatar';
  const [dadosCompletos, setDadosCompletos] = useState(null);
  const [amigos, setAmigos] = useState([]);
  const [imageLoaded, setImageLoaded] = useState(false);
  const { user } = useContext(AuthContext);

  const dadosExibicao = {
    ...dadosCompletos,
    foto: user?.foto || dadosCompletos?.foto,
    amigos: amigos
  } || usuario;

  const getImageUrl = (url) => {
    if (!url) return null;
    return `${url}?t=${new Date().getTime()}`;
  };

  const getDefaultAvatar = (nome) => {
    return `https://ui-avatars.com/api/?name=${encodeURIComponent(nome || 'User')}&background=random&size=200&format=svg`;
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: 'long',
      year: 'numeric'
    });
  };

  // Pré-carregar avatar padrão
  useEffect(() => {
    const preloadDefaultAvatar = () => {
      const img = new Image();
      img.src = getDefaultAvatar(dadosExibicao?.nome);
    };
    preloadDefaultAvatar();
  }, [dadosExibicao?.nome]);

  useEffect(() => {
    const carregarDados = async () => {
      try {
        const [perfilData, favoritosData, amigosData] = await Promise.all([
          authService.getUserProfile(),
          authService.getFavorites(),
          amigoService.listarAmigos()
        ]);

        if (perfilData?.foto) {
          const img = new Image();
          img.src = getImageUrl(perfilData.foto);
        }

        setDadosCompletos({
          ...perfilData,
          foto: perfilData?.foto ? getImageUrl(perfilData.foto) : null,
          favoritos: favoritosData,
          amigos: amigosData
        });

        if (Array.isArray(amigosData)) {
          setAmigos(amigosData);
        }
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
      }
    };

    carregarDados();
  }, [user?.foto]);

  return (
    <div className="bg-darker rounded-lg p-6 max-w-4xl mx-auto">
      {/* Header do Perfil */}
      <div className="flex flex-col md:flex-row items-start gap-8">
        {/* Coluna da Esquerda - Foto e Info Básica */}
        <div className="flex flex-col items-center space-y-4 md:w-1/3">
          <div className="w-40 h-40 rounded-full overflow-hidden border-4 border-primary relative">
            {!imageLoaded && (
              <div className="absolute inset-0 bg-dark flex items-center justify-center">
                <div className="animate-pulse w-full h-full bg-gray-700" />
              </div>
            )}
            <img
              src={getImageUrl(dadosExibicao?.foto) || getDefaultAvatar(dadosExibicao?.nome)}
              alt={dadosExibicao?.nome}
              className={`w-full h-full object-cover transition-opacity duration-300 ${
                imageLoaded ? 'opacity-100' : 'opacity-0'
              }`}
              onLoad={() => setImageLoaded(true)}
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = getDefaultAvatar(dadosExibicao?.nome);
                setImageLoaded(true);
              }}
              loading="eager"
            />
          </div>
          
          <div className="text-center">
            <h1 className="text-2xl font-bold text-white">{dadosExibicao?.nome}</h1>
            <div className="text-gray-400 text-sm mt-2 space-y-1">
              <p>Membro desde {formatarData(dadosExibicao?.data_criacao)}</p>
              <p>{amigos.length} amigos</p>
              <p>{dadosExibicao?.favoritos?.length || 0} favoritos</p>
            </div>
          </div>
        </div>

        {/* Coluna da Direita - Descrição e Stats */}
        <div className="md:w-2/3 space-y-6">
          <div className="bg-dark p-6 rounded-lg">
            <h2 className="text-xl font-semibold mb-4 text-primary">Sobre</h2>
            <p className="text-gray-300">
              {dadosExibicao?.descricao || "Nenhuma descrição disponível."}
            </p>
          </div>
        </div>
      </div>

      {/* Seção de Favoritos */}
      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-4 text-primary border-b border-gray-700 pb-2">
          Favoritos ({dadosExibicao?.favoritos?.length || 0})
        </h2>
        
        {dadosExibicao?.favoritos?.length > 0 ? (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {dadosExibicao.favoritos.map(item => (
              <Link
                key={item._id}
                to={`/detalhes/${item.tipo}/${item._id}`}
                className="transform hover:scale-105 transition-all duration-300"
              >
                <div className="bg-dark rounded-lg overflow-hidden h-full">
                  <div className="relative pb-[150%]">
                    <img
                      src={item.img_url}
                      alt={item.titulo}
                      className="absolute inset-0 w-full h-full object-cover"
                      loading="lazy"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.jpg';
                      }}
                    />
                  </div>
                  <div className="p-3">
                    <h3 className="text-sm font-medium text-gray-200 line-clamp-2 h-10">
                      {item.titulo}
                    </h3>
                    <p className="text-xs text-gray-400 mt-1 capitalize">
                      {item.tipo}
                    </p>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-400 bg-dark rounded-lg">
            <p>Nenhum favorito adicionado ainda</p>
            <p className="text-sm mt-2">Adicione itens aos favoritos para vê-los aqui</p>
          </div>
        )}
      </div>
    </div>
  );
}
