import { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import api, { authService } from '../services/api';

export function Detalhes() {
  const { collection, id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    loadDetails();
  }, [id, collection]);

  async function loadDetails() {
    try {
      setLoading(true);
      const response = await api.get(`/api/item/${collection}/${id}`);
      setItem(response.data);
    } catch (error) {
      setError('Erro ao carregar detalhes');
      console.error('Erro:', error);
    } finally {
      setLoading(false);
    }
  }

  const handleFavorite = async () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      await authService.toggleFavorite(id, collection);
      // Adicionar feedback visual
    } catch (error) {
      console.error('Erro ao favoritar:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (error || !item) {
    return (
      <div className="container mx-auto px-4 pt-20">
        <p className="text-light text-center">{error || 'Item não encontrado'}</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 pt-20">
      <div className="flex flex-col md:flex-row gap-8">
        <div className="w-full md:w-1/3">
          <img 
            src={item.img_url} 
            alt={item.titulo}
            className="w-full rounded-lg shadow-lg"
          />
        </div>
        
        <div className="flex-1">
          <div className="flex justify-between items-start">
            <h1 className="text-4xl font-bold text-light mb-4">{item.titulo}</h1>
            {isAuthenticated && (
              <button 
                onClick={handleFavorite}
                className="text-3xl hover:text-primary transition-colors"
              >
                ❤️
              </button>
            )}
          </div>
          
          {item.categoria && (
            <div className="mb-4">
              <span className="bg-primary text-white px-3 py-1 rounded-full text-sm">
                {item.categoria}
              </span>
            </div>
          )}
          
          {item.descricao && (
            <p className="text-light mb-6">{item.descricao}</p>
          )}
          
          <a
            href={item.url}
            target="_blank"
            rel="noopener noreferrer"
            className="bg-primary text-white px-6 py-3 rounded-lg hover:bg-hover transition-colors inline-flex items-center"
          >
            Assistir Agora
          </a>
        </div>
      </div>
    </div>
  );
}
