import { useState, useRef, useEffect } from 'react';
import { Card } from './Card';

export const Carousel = ({ items, title }) => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [maxScroll, setMaxScroll] = useState(0);
  const trackRef = useRef(null);
  const cardWidth = 280; // Largura fixa do card + gap

  useEffect(() => {
    if (trackRef.current) {
      const containerWidth = trackRef.current.parentElement.offsetWidth;
      const totalWidth = items.length * cardWidth;
      setMaxScroll(Math.max(0, totalWidth - containerWidth));
    }
  }, [items]);

  const scroll = (direction) => {
    const visibleCards = Math.floor(trackRef.current.parentElement.offsetWidth / cardWidth);
    const scrollAmount = direction === 'left' ? -1 : 1;
    const moveBy = scrollAmount * (cardWidth * Math.floor(visibleCards / 2));
    
    setScrollPosition(prev => {
      const newPosition = prev + moveBy;
      return Math.max(0, Math.min(newPosition, maxScroll));
    });
  };

  if (!items?.length) return null;

  return (
    <section className="mt-16 relative px-4">
      <h2 className="text-2xl font-bold mb-6 text-light pl-4 border-l-4 border-primary">
        {title || 'Sugestões'}
      </h2>
      
      <div className="relative group">
        {scrollPosition > 0 && (
          <button 
            onClick={() => scroll('left')}
            className="carousel-nav-btn left-0"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
        )}

        <div className="overflow-hidden">
          <div 
            ref={trackRef}
            className="flex gap-4"
            style={{
              transform: `translateX(-${scrollPosition}px)`,
              transition: 'transform 0.5s ease-out'
            }}
          >
            {items.map((item, index) => (
              <div 
                key={item._id || index} 
                className="flex-none w-[260px]"
                style={{ marginRight: '20px' }}
              >
                <Card item={item} />
              </div>
            ))}
          </div>
        </div>

        {scrollPosition < maxScroll && (
          <button 
            onClick={() => scroll('right')}
            className="carousel-nav-btn right-0"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        )}
      </div>
    </section>
  );
};
