import { Link } from 'react-router-dom';

export function Card({ item, onFavorite, isAuthenticated }) {
  return (
    <Link 
      to={`/detalhes/${item.collection}/${item._id}`}
      className="netflix-card group"
    >
      <div className="aspect-[2/3] relative overflow-hidden rounded-lg">
        <img 
          src={item.img_url} 
          alt={item.titulo}
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-all duration-300">
          <div className="absolute bottom-0 w-full p-4 transform translate-y-2 group-hover:translate-y-0 transition-transform">
            <h3 className="text-lg font-bold text-white line-clamp-2 group-hover:text-primary transition-colors">
              {item.titulo}
            </h3>
            <div className="flex items-center gap-2 mt-2">
              <button 
                className="bg-primary hover:bg-hover text-white px-4 py-2 rounded-lg text-sm font-bold transition-all transform hover:scale-105"
                onClick={(e) => {
                  e.preventDefault();
                  window.location.href = item.url;
                }}
              >
                Assistir
              </button>
              {isAuthenticated && (
                <button 
                  onClick={(e) => {
                    e.preventDefault();
                    onFavorite?.(item);
                  }}
                  className="text-2xl hover:scale-110 transition-transform"
                >
                  ❤️
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </Link>
  );
}
