import React, { useState } from 'react';
import { ListaConversas } from '../components/ListaConversas';
import { Chat } from '../components/Chat';
import { Footer } from '../components/Footer';

export function Mensagens() {
  const [conversaAtual, setConversaAtual] = useState(null);

  return (
    <div className="flex flex-col min-h-screen">
      <div className="content-section pt-20 animate-fadeIn flex-grow">
        <div className="container mx-auto px-4 pt-24 pb-12">
          <div className="flex space-x-4 h-[calc(100vh-200px)]">
            <ListaConversas onSelectConversation={setConversaAtual} />
            
            {conversaAtual ? (
              <div className="flex-1">
                <Chat 
                  amigoId={conversaAtual._id} 
                  amigoNome={conversaAtual.nome} 
                />
              </div>
            ) : (
              <div className="flex-1 bg-darker rounded-lg flex items-center justify-center">
                <p className="text-gray-400">
                  Selecione uma conversa para come√ßar
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
