import { useNavigate } from 'react-router-dom';

export function Card({ item, isAuthenticated, onFavorite }) {
  const navigate = useNavigate();

  return (
    <div className="card-container relative">
      <img 
        src={item.img_url || item.img_capa_url || '/images/placeholder.jpg'} 
        alt={item.titulo}
        className="w-full h-full object-cover transition-transform duration-300"
      />
      
      <div className="card-overlay">
        <div className="absolute top-2 right-2 z-10">
          {isAuthenticated && (
            <button
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                onFavorite(item);
              }}
              className="favorite-button text-white hover:text-primary"
              title={item.isFavorito ? "Remover dos favoritos" : "Adicionar aos favoritos"}
            >
              <svg
                className={`w-6 h-6 ${item.isFavorito ? 'text-primary' : ''}`}
                fill={item.isFavorito ? 'currentColor' : 'none'}
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                />
              </svg>
            </button>
          )}
        </div>

        <div className="absolute inset-0 flex items-center justify-center">
          <button
            onClick={() => navigate(`/detalhes/${item.collection}/${item._id}`)}
            className="watch-button"
          >
            Assistir
          </button>
        </div>
      </div>
    </div>
  );
}
