import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { authService } from '../services/api';

export function VisualizarPerfil({ usuario }) {
  const FALLBACK_AVATAR = 'https://via.placeholder.com/200x200?text=Avatar';
  const [dadosCompletos, setDadosCompletos] = useState(null);

  useEffect(() => {
    const carregarDadosCompletos = async () => {
      try {
        const data = await authService.getUserProfile();
        setDadosCompletos(data);
      } catch (error) {
        console.error('Erro ao carregar dados do perfil:', error);
      }
    };

    carregarDadosCompletos();
  }, [usuario?.id]);

  const getDefaultAvatar = (nome) => {
    return `https://ui-avatars.com/api/?name=${encodeURIComponent(nome || 'User')}&background=random&size=200`;
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: 'long',
      year: 'numeric'
    });
  };

  const dadosExibicao = dadosCompletos || usuario;

  return (
    <div className="bg-darker rounded-lg p-6 max-w-4xl mx-auto">
      {/* Header do Perfil */}
      <div className="flex flex-col md:flex-row items-start gap-8">
        {/* Coluna da Esquerda - Foto e Info Básica */}
        <div className="flex flex-col items-center space-y-4 md:w-1/3">
          <div className="w-40 h-40 rounded-full overflow-hidden border-4 border-primary">
            <img
              src={dadosExibicao?.foto || getDefaultAvatar(dadosExibicao?.nome)}
              alt={dadosExibicao?.nome}
              className="w-full h-full object-cover"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = FALLBACK_AVATAR;
              }}
            />
          </div>
          
          <div className="text-center">
            <h1 className="text-2xl font-bold text-white">{dadosExibicao?.nome}</h1>
            <div className="text-gray-400 text-sm mt-2 space-y-1">
              <p>Membro desde {formatarData(dadosExibicao?.data_criacao)}</p>
              <p>{dadosExibicao?.amigos?.length || 0} amigos</p>
              <p>{dadosExibicao?.favoritos?.length || 0} favoritos</p>
            </div>
          </div>
        </div>

        {/* Coluna da Direita - Descrição e Stats */}
        <div className="md:w-2/3 space-y-6">
          {/* Seção Sobre */}
          <div className="bg-dark p-6 rounded-lg">
            <h2 className="text-xl font-semibold mb-4 text-primary">Sobre</h2>
            <p className="text-gray-300">
              {dadosExibicao?.descricao || "Nenhuma descrição disponível."}
            </p>
          </div>

          {/* Seção Estatísticas */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            {/* Cor Favorita */}
            <div className="bg-dark p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-400 mb-2">Cor Favorita</h3>
              <div className="flex items-center gap-2">
                <div 
                  className="w-6 h-6 rounded border border-gray-600"
                  style={{ backgroundColor: dadosExibicao?.tema_cor || '#ef4444' }}
                ></div>
                <span className="text-light">{dadosExibicao?.tema_cor || '#ef4444'}</span>
              </div>
            </div>

            {/* Amigos */}
            <div className="bg-dark p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-400 mb-2">Amigos</h3>
              <div className="flex items-center gap-2">
                <svg className="w-5 h-5 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
                <span className="text-xl font-semibold text-light">{dadosExibicao?.amigos?.length || 0}</span>
              </div>
            </div>

            {/* Favoritos */}
            <div className="bg-dark p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-400 mb-2">Favoritos</h3>
              <div className="flex items-center gap-2">
                <svg className="w-5 h-5 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
                </svg>
                <span className="text-xl font-semibold text-light">{dadosExibicao?.favoritos?.length || 0}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Seção de Favoritos */}
      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-4 text-primary border-b border-gray-700 pb-2">
          Favoritos
        </h2>
        
        {dadosExibicao?.favoritos?.length > 0 ? (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {dadosExibicao.favoritos.map(item => (
              <Link
                key={item._id}
                to={`/detalhes/${item.tipo}/${item._id}`}
                className="transform hover:scale-105 transition-all duration-300"
              >
                <div className="bg-dark rounded-lg overflow-hidden">
                  <div className="relative pb-[150%]">
                    <img
                      src={item.img_url}
                      alt={item.titulo}
                      className="absolute inset-0 w-full h-full object-cover"
                      loading="lazy"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/placeholder.jpg';
                      }}
                    />
                  </div>
                  <div className="p-3">
                    <h3 className="text-sm font-medium text-gray-200 line-clamp-2">
                      {item.titulo}
                    </h3>
                    <p className="text-xs text-gray-400 mt-1 capitalize">
                      {item.tipo}
                    </p>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-400">
            Nenhum favorito adicionado ainda
          </div>
        )}
      </div>
    </div>
  );
}
