import React, { useState } from 'react';
import { Alert } from './Alert';

export function EditarPerfil({ usuario, onSalvar }) {
  const [perfil, setPerfil] = useState({
    nome: usuario.nome,
    descricao: usuario.descricao || '',
    foto: usuario.foto || ''
  });
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(null);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        setAlert({
          type: 'error',
          message: 'A imagem deve ter no máximo 2MB'
        });
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setPerfil(prev => ({ ...prev, foto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      await onSalvar(perfil);
      setAlert({
        type: 'success',
        message: 'Perfil atualizado com sucesso!'
      });
    } catch (error) {
      setAlert({
        type: 'error',
        message: error.message
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-darker rounded-lg shadow-xl">
      {alert && (
        <Alert
          message={alert.message}
          type={alert.type}
          onClose={() => setAlert(null)}
        />
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="space-y-4">
          <div className="flex justify-center">
            <div className="relative">
              <img
                src={perfil.foto || '/images/default-avatar.png'}
                alt="Foto de perfil"
                className="w-32 h-32 rounded-full object-cover border-4 border-primary"
              />
              <label
                htmlFor="foto"
                className="absolute bottom-0 right-0 bg-primary text-white p-2 rounded-full cursor-pointer hover:bg-hover"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
                </svg>
              </label>
              <input
                type="file"
                id="foto"
                accept="image/*"
                className="hidden"
                onChange={handleImageChange}
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">Nome</label>
            <input
              type="text"
              value={perfil.nome}
              onChange={(e) => setPerfil({ ...perfil, nome: e.target.value })}
              className="w-full p-3 rounded bg-dark text-light"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">Descrição</label>
            <textarea
              value={perfil.descricao}
              onChange={(e) => setPerfil({ ...perfil, descricao: e.target.value })}
              className="w-full p-3 rounded bg-dark text-light h-32 resize-none"
              placeholder="Conte um pouco sobre você..."
            />
          </div>
        </div>

        <button
          type="submit"
          disabled={loading}
          className="w-full py-3 bg-primary text-white rounded hover:bg-hover transition-all disabled:opacity-50"
        >
          {loading ? 'Salvando...' : 'Salvar Alterações'}
        </button>
      </form>
    </div>
  );
}
