import { createContext, useContext, useState, useEffect } from 'react';

const DEFAULT_PRIMARY_COLOR = '#ef4444';
const DEFAULT_HOVER_COLOR = '#b2070f';

const ThemeContext = createContext();

export function ThemeProvider({ children }) {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const savedTheme = localStorage.getItem('theme');
    return user?.theme ? user.theme === 'dark' : savedTheme ? savedTheme === 'dark' : true;
  });

  const [themeColor, setThemeColor] = useState(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return user?.tema_cor || localStorage.getItem('themeColor') || DEFAULT_PRIMARY_COLOR;
  });

  const resetToDefaultColors = () => {
    setThemeColor(DEFAULT_PRIMARY_COLOR);
    document.documentElement.style.setProperty('--color-primary', DEFAULT_PRIMARY_COLOR);
    document.documentElement.style.setProperty('--color-hover', DEFAULT_HOVER_COLOR);
  };

  const toggleTheme = (newTheme) => {
    const themeValue = typeof newTheme === 'boolean' ? newTheme : !isDarkMode;
    setIsDarkMode(themeValue);
    localStorage.setItem('theme', themeValue ? 'dark' : 'light');
    document.documentElement.classList.toggle('dark', themeValue);

    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!localStorage.getItem('token')) {
      resetToDefaultColors();
    } else if (user.tema_cor) {
      document.documentElement.style.setProperty('--color-primary', user.tema_cor);
      document.documentElement.style.setProperty('--color-hover', user.tema_cor);
    }
  };

  const updateThemeColor = (color) => {
    setThemeColor(color);
    localStorage.setItem('themeColor', color);
    document.documentElement.style.setProperty('--color-primary', color);
    document.documentElement.style.setProperty('--color-hover', color);
  };

  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
    document.documentElement.style.setProperty('--color-primary', themeColor);
    document.documentElement.style.setProperty('--color-hover', themeColor);
  }, [isDarkMode, themeColor]);

  return (
    <ThemeContext.Provider value={{ 
      isDarkMode, 
      toggleTheme, 
      themeColor, 
      setThemeColor: updateThemeColor,
      resetToDefaultColors 
    }}>
      {children}
    </ThemeContext.Provider>
  );
}

export const useTheme = () => useContext(ThemeContext);
