import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001'  // Removido o /api da baseURL
});

api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Adicionar tratamento de erro global
api.interceptors.response.use(
  response => response,
  error => {
    console.error('API Error:', error);
    if (error.response?.status === 400) {
      console.error('Bad Request:', error.response.data);
    }
    return Promise.reject(error);
  }
);

export const authService = {
  async login(email, senha) {
    const { data } = await api.post('/api/auth/login', { email, senha });
    return data;
  },

  async register(nome, email, senha) {
    const { data } = await api.post('/api/auth/register', { nome, email, senha });
    return data;
  },

  async toggleFavorite(conteudoId, tipo) {
    const { data } = await api.post('/api/auth/favoritos', { conteudo_id: conteudoId, tipo });
    return data;
  },

  async getFavorites() {
    const { data } = await api.get('/api/auth/favoritos');
    return data;
  }
};

export const contentService = {
  async getRecentContent() {
    const { data } = await api.get('/api/recent');
    return data;
  },

  async searchContent(query) {
    const { data } = await api.get(`/api/search?q=${encodeURIComponent(query)}`);
    return data;
  },

  async getItem(collection, id) {
    const { data } = await api.get(`/api/item/${collection}/${id}`);
    return { ...data, collection };
  },

  async getRecommendations(collection) {
    const { data } = await api.get(`/api/random/${collection}`);
    return data.map(item => ({ ...item, collection }));
  }
};

export default api;
