import { useState, useRef, useEffect } from 'react';
import { Card } from './Card';

export const Carousel = ({ items, title }) => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const [maxScroll, setMaxScroll] = useState(0);
  const trackRef = useRef(null);
  const cardWidth = 280; // Largura fixa do card + gap

  const [currentSlide, setCurrentSlide] = useState(0);
  const [itemsPerSlide, setItemsPerSlide] = useState(6);

  useEffect(() => {
    if (trackRef.current) {
      const containerWidth = trackRef.current.parentElement.offsetWidth;
      const totalWidth = items.length * cardWidth;
      setMaxScroll(Math.max(0, totalWidth - containerWidth));
    }
  }, [items]);

  useEffect(() => {
    const updateItemsPerSlide = () => {
      const width = window.innerWidth;
      if (width < 640) setItemsPerSlide(2);
      else if (width < 768) setItemsPerSlide(3);
      else if (width < 1024) setItemsPerSlide(4);
      else setItemsPerSlide(6);
    };

    updateItemsPerSlide();
    window.addEventListener('resize', updateItemsPerSlide);
    return () => window.removeEventListener('resize', updateItemsPerSlide);
  }, []);

  const scroll = (direction) => {
    const visibleCards = Math.floor(trackRef.current.parentElement.offsetWidth / cardWidth);
    const scrollAmount = direction === 'left' ? -1 : 1;
    const moveBy = scrollAmount * (cardWidth * Math.floor(visibleCards / 2));
    
    setScrollPosition(prev => {
      const newPosition = prev + moveBy;
      return Math.max(0, Math.min(newPosition, maxScroll));
    });
  };

  const nextSlide = () => {
    const maxSlides = Math.ceil(items.length / itemsPerSlide) - 1;
    setCurrentSlide(current => current >= maxSlides ? 0 : current + 1);
  };

  const prevSlide = () => {
    const maxSlides = Math.ceil(items.length / itemsPerSlide) - 1;
    setCurrentSlide(current => current <= 0 ? maxSlides : current - 1);
  };

  if (!items?.length) return null;

  return (
    <section className="mt-16 relative group">
      <h2 className="text-2xl font-bold mb-6 text-light pl-4 border-l-4 border-primary">
        {title || 'Sugestões'}
      </h2>
      
      <div className="relative overflow-hidden">
        <div className="carousel-track flex transition-transform duration-500 ease-out"
             style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
          {items.map((item, index) => (
            <div key={item._id || index} 
                 className="flex-none w-1/2 sm:w-1/3 md:w-1/4 lg:w-1/6 px-2">
              <Card item={item} />
            </div>
          ))}
        </div>

        {currentSlide > 0 && (
          <button onClick={prevSlide}
                  className="carousel-nav-btn left-2">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
        )}

        {currentSlide < Math.ceil(items.length / itemsPerSlide) - 1 && (
          <button onClick={nextSlide}
                  className="carousel-nav-btn right-2">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        )}
      </div>
    </section>
  );
};
