import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { amigoService } from '../services/api';

export function FriendsDropdown({ onClose }) {
  const [amigos, setAmigos] = useState([]);
  const [loading, setLoading] = useState(true);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    carregarAmigos();
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      onClose();
    }
  };

  const carregarAmigos = async () => {
    try {
      const data = await amigoService.listarAmigos();
      setAmigos(data);
    } catch (error) {
      console.error('Erro ao carregar amigos:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div 
      ref={dropdownRef}
      className="absolute left-0 w-64 mt-2 bg-darker border border-gray-700 rounded-lg shadow-xl z-50"
      style={{ top: '100%' }}
    >
      {/* Adicionar uma seta decorativa */}
      <div className="absolute -top-2 left-4 w-4 h-4 bg-darker border-l border-t border-gray-700 transform rotate-45"></div>

      {/* Opções principais */}
      <div className="p-2 border-b border-gray-700">
        <Link
          to="/amigos"
          className="block w-full text-left px-3 py-2 text-sm text-light hover:bg-hover rounded transition-all"
        >
          Ver Todos os Amigos
        </Link>
        <Link
          to="/amigos/adicionar"
          className="block w-full text-left px-3 py-2 text-sm text-light hover:bg-hover rounded transition-all"
        >
          Adicionar Amigos
        </Link>
      </div>

      {/* Lista de amigos */}
      <div className="p-2">
        <h3 className="text-xs font-semibold text-gray-400 px-3 mb-2">
          Amigos Recentes
        </h3>
        
        <div className="max-h-48 overflow-y-auto custom-scrollbar">
          {loading ? (
            <div className="text-center py-4">
              <div className="animate-spin h-5 w-5 border-2 border-primary border-t-transparent rounded-full mx-auto"></div>
            </div>
          ) : amigos.length > 0 ? (
            amigos.map(amigo => (
              <Link
                key={amigo.usuario._id}
                to={`/perfil/${amigo.usuario._id}`}
                className="flex items-center gap-3 px-3 py-2 hover:bg-hover rounded transition-all"
              >
                <div className="w-8 h-8 rounded-full overflow-hidden bg-gray-700 flex-shrink-0">
                  <img
                    src={amigo.usuario.foto || '/images/default-avatar.png'}
                    alt={amigo.usuario.nome}
                    className="w-full h-full object-cover"
                  />
                </div>
                <span className="text-sm text-light truncate">
                  {amigo.usuario.nome}
                </span>
              </Link>
            ))
          ) : (
            <p className="text-sm text-gray-400 text-center py-4">
              Nenhum amigo adicionado
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
