import React from 'react';
import { Link } from 'react-router-dom';

export function Card({ item }) {
  return (
    <div className="card">
      <Link to={`/detalhes/${item.collection}/${item._id}`} className="block w-full h-full">
        <img
          src={item.img_url || '/images/placeholder.jpg'}
          alt={item.titulo}
          className="w-full h-full object-cover"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = '/images/placeholder.jpg';
          }}
        />
        <div className="hover-overlay">
          <span className="opacity-0 group-hover:opacity-100 transform scale-0 group-hover:scale-100 transition-all duration-300 bg-primary text-white px-4 py-2 rounded hover:bg-hover">
            Assistir
          </span>
        </div>
      </Link>
    </div>
  );
}
