import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { authService } from '../services/api';
import { Footer } from '../components/Footer';

export function Favoritos() {
  const [favoritos, setFavoritos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadFavoritos = async () => {
      try {
        setLoading(true);
        const data = await authService.getFavorites();
        setFavoritos(data);
      } catch (error) {
        console.error('Erro ao carregar favoritos:', error);
        setError('Erro ao carregar seus favoritos. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    loadFavoritos();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="flex flex-col min-h-screen">
      <div className="content-section pt-20 flex-grow">
        {/* Adicionar botão voltar */}
        <Link 
          to="/perfil" 
          className="text-primary hover:text-hover transition-colors duration-300 flex items-center mb-4"
        >
          <svg className="w-5 h-5 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"/>
          </svg>
          Perfil
        </Link>

        <h1 className="section-title">Meus Favoritos</h1>
        
        {favoritos.length === 0 ? (
          <div className="flex flex-col items-center justify-center p-8">
            <p className="text-xl text-light mb-4">Você ainda não tem favoritos.</p>
            <p className="text-light">Explore o catálogo e adicione itens aos favoritos!</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {favoritos.map(item => (
              <div key={item._id} className="relative group aspect-[2/3]">
                <Link 
                  to={`/detalhes/${item._id}`} 
                  className="block w-full h-full"
                >
                  <div className="relative w-full h-full">
                    <img 
                      src={item.img_url || '/images/placeholder.jpg'} 
                      alt={item.titulo} 
                      className="absolute inset-0 w-full h-full object-cover rounded-lg transition-transform duration-300 group-hover:scale-105"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
                      <span className="text-white font-bold text-lg p-2 text-center">{item.titulo}</span>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}
