import { useState, useEffect } from 'react';
import { authService } from '../services/api';

export function Favoritos() {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadFavorites();
  }, []);

  async function loadFavorites() {
    try {
      const data = await authService.getFavorites();
      setFavorites(data);
    } catch (error) {
      console.error('Erro ao carregar favoritos:', error);
    } finally {
      setLoading(false);
    }
  }

  async function handleRemoveFavorite(id, tipo) {
    try {
      await authService.toggleFavorite(id, tipo);
      setFavorites(favorites.filter(item => item._id !== id));
    } catch (error) {
      console.error('Erro ao remover favorito:', error);
    }
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 pt-20">
      <h1 className="text-3xl font-bold text-light mb-8">Meus Favoritos</h1>
      {favorites.length === 0 ? (
        <p className="text-light text-center">Você ainda não tem favoritos.</p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {favorites.map(item => (
            <div key={item._id} className="relative group">
              <img 
                src={item.img_url} 
                alt={item.titulo}
                className="w-full h-64 object-cover rounded-lg"
              />
              <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                <button className="bg-primary text-white px-4 py-2 rounded hover:bg-hover transition-colors mb-2">
                  Assistir
                </button>
                <button 
                  onClick={() => handleRemoveFavorite(item._id, item.tipo)}
                  className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors"
                >
                  Remover
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
