import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001/api'  // Adicionado /api na baseURL
});

api.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Adicionar tratamento de erro global
api.interceptors.response.use(
  response => response,
  error => {
    console.error('API Error:', error);
    if (error.response?.status === 400) {
      console.error('Bad Request:', error.response.data);
    }
    return Promise.reject(error);
  }
);

export const authService = {
  async login(email, senha) {
    const { data } = await api.post('/auth/login', { email, senha });
    return data;
  },

  async register(nome, email, senha) {
    const { data } = await api.post('/auth/register', { nome, email, senha });
    return data;
  },

  async toggleFavorite(conteudoId, tipo) {
    const { data } = await api.post('/auth/favoritos', { conteudo_id: conteudoId, tipo });
    return data;
  },

  async getFavorites() {
    const { data } = await api.get('/auth/favoritos');
    return data;
  }
};

export const contentService = {
  async getItem(collection, id) {
    try {
      const { data } = await api.get(`/item/${collection}/${id}`);
      return { ...data, collection };
    } catch (error) {
      console.error('Erro ao buscar item:', error);
      throw error;
    }
  },

  async getRecommendations(collection) {
    try {
      const { data } = await api.get(`/random/${collection}`);
      return data.map(item => ({ ...item, collection }));
    } catch (error) {
      console.error('Erro ao buscar recomendações:', error);
      throw error;
    }
  }
};

export default api;
