import React, { useState, useEffect, useRef } from 'react';
import { mensagemService } from '../services/api';
import { io } from 'socket.io-client';
import { useAlert } from '../contexts/AlertContext';

export function Chat({ amigoId, amigoNome }) {
  const [mensagens, setMensagens] = useState([]);
  const [novaMensagem, setNovaMensagem] = useState('');
  const [loading, setLoading] = useState(false);
  const chatRef = useRef(null);
  const socketRef = useRef(null);
  const { showAlert } = useAlert();

  useEffect(() => {
    const token = localStorage.getItem('token');
    socketRef.current = io(process.env.REACT_APP_API_URL || 'http://localhost:3001', {
      auth: { token }
    });

    socketRef.current.on('nova_mensagem', (data) => {
      if (data.mensagem.remetente === amigoId) {
        setMensagens(msgs => [...msgs, data.mensagem]);
        chatRef.current?.scrollIntoView({ behavior: 'smooth' });
      }
    });

    carregarMensagens();

    return () => socketRef.current.disconnect();
  }, [amigoId]);

  const carregarMensagens = async () => {
    try {
      setLoading(true);
      const data = await mensagemService.buscarMensagens(amigoId);
      setMensagens(data);
      chatRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch (error) {
      showAlert('Erro ao carregar mensagens', 'error');
    } finally {
      setLoading(false);
    }
  };

  const enviarMensagem = async (e) => {
    e.preventDefault();
    if (!novaMensagem.trim()) return;

    try {
      const mensagem = await mensagemService.enviarMensagem(amigoId, novaMensagem);
      setMensagens(msgs => [...msgs, mensagem]);
      setNovaMensagem('');
      chatRef.current?.scrollIntoView({ behavior: 'smooth' });
    } catch (error) {
      showAlert('Erro ao enviar mensagem', 'error');
    }
  };

  return (
    <div className="flex flex-col h-[calc(100vh-200px)] bg-darker rounded-lg">
      <div className="p-4 bg-dark border-b border-gray-700">
        <h2 className="text-xl font-semibold">{amigoNome}</h2>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {loading ? (
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-primary"></div>
          </div>
        ) : (
          mensagens.map((msg, index) => (
            <div
              key={index}
              className={`flex ${msg.remetente === amigoId ? 'justify-start' : 'justify-end'}`}
            >
              <div
                className={`max-w-[70%] rounded-lg p-3 ${
                  msg.remetente === amigoId
                    ? 'bg-dark text-white'
                    : 'bg-primary text-white'
                }`}
              >
                <p>{msg.conteudo}</p>
                <span className="text-xs opacity-70">
                  {new Date(msg.data_envio).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))
        )}
        <div ref={chatRef} />
      </div>

      <form onSubmit={enviarMensagem} className="p-4 border-t border-gray-700">
        <div className="flex space-x-2">
          <input
            type="text"
            value={novaMensagem}
            onChange={(e) => setNovaMensagem(e.target.value)}
            placeholder="Digite sua mensagem..."
            className="flex-1 bg-dark text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-primary"
          />
          <button
            type="submit"
            disabled={!novaMensagem.trim()}
            className="bg-primary text-white px-6 py-2 rounded-lg hover:bg-hover disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
}
