import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { authService } from '../services/api';
import { VisualizarPerfil } from '../components/VisualizarPerfil';

export function PerfilAmigo() {
  const { id } = useParams();
  const [amigo, setAmigo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const carregarPerfilAmigo = async () => {
      try {
        setLoading(true);
        const data = await authService.getPublicProfile(id);
        setAmigo(data);
      } catch (error) {
        setError('Não foi possível carregar o perfil deste amigo.');
        console.error('Erro ao carregar perfil do amigo:', error);
      } finally {
        setLoading(false);
      }
    };

    carregarPerfilAmigo();
  }, [id]);

  if (loading) {
    return <div className="text-center py-8">Carregando perfil...</div>;
  }

  if (error) {
    return <div className="text-center py-8 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 pt-24">
      <VisualizarPerfil usuario={amigo} />
    </div>
  );
}
