import { useState, useEffect } from 'react';
import { authService } from '../services/api';

export function Perfil() {
  const [userData, setUserData] = useState({ nome: '', email: '', descricao: '', foto: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchUserData() {
      try {
        setLoading(true);
        const data = await authService.getUserProfile();
        setUserData(data);
      } catch (err) {
        setError('Erro ao carregar dados do perfil.');
      } finally {
        setLoading(false);
      }
    }
    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      await authService.updateUserProfile(userData);
      alert('Perfil atualizado com sucesso!');
    } catch (err) {
      setError('Erro ao atualizar perfil.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Carregando...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Meu Perfil</h1>
      <div className="flex flex-col gap-4">
        <label>
          Nome:
          <input
            type="text"
            name="nome"
            value={userData.nome}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            disabled
          />
        </label>
        <label>
          Descrição:
          <textarea
            name="descricao"
            value={userData.descricao}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
          />
        </label>
        <label>
          Foto de Perfil (URL):
          <input
            type="text"
            name="foto"
            value={userData.foto}
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
          />
        </label>
        <button
          onClick={handleSave}
          className="bg-primary text-white px-4 py-2 rounded hover:bg-hover transition-colors"
        >
          Salvar
        </button>
      </div>
    </div>
  );
}
