import React, { useState, useContext } from 'react';
import { Alert } from './Alert';
import { AuthContext } from '../contexts/AuthContext';
import { authService } from '../services/api';
import { useAlert } from '../contexts/AlertContext';
import { useTheme } from '../contexts/ThemeContext';

export function EditarPerfil({ usuario }) {
  const FALLBACK_AVATAR = 'https://via.placeholder.com/200x200?text=Avatar';

  const getDefaultAvatar = (nome) => {
    return `https://ui-avatars.com/api/?name=${encodeURIComponent(nome || 'User')}&background=random&size=200`;
  };

  const defaultProfile = {
    nome: '',
    descricao: '',
    foto: null,
    tema_cor: '#ef4444',
    id: usuario?.id
  };

  const [perfil, setPerfil] = useState(() => {
    const savedUser = JSON.parse(localStorage.getItem('user') || '{}');
    return {
      nome: savedUser.nome || usuario?.nome || '',
      descricao: savedUser.descricao || usuario?.descricao || '',
      foto: savedUser.foto || usuario?.foto || null,
      tema_cor: savedUser.tema_cor || usuario?.tema_cor || '#ef4444'
    };
  });

  const [loading, setLoading] = useState(false);
  const { setThemeColor } = useTheme();
  const { updateUserData } = useContext(AuthContext);
  const { showAlert } = useAlert();

  const handleSave = async () => {
    try {
      setLoading(true);
      const response = await authService.updateUserProfile(perfil);

      const updatedUser = {
        ...response.user,
        foto: perfil.foto
      };

      updateUserData(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setThemeColor(perfil.tema_cor);
      showAlert('Perfil atualizado com sucesso!', 'success');
    } catch (error) {
      showAlert(error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleReset = async () => {
    if (window.confirm('Tem certeza que deseja restaurar seu perfil para o padrão? Esta ação não poderá ser desfeita.')) {
      try {
        setLoading(true);
        setPerfil(defaultProfile);
        setThemeColor(defaultProfile.tema_cor);

        const updateData = {
          nome: defaultProfile.nome,
          descricao: defaultProfile.descricao,
          foto: defaultProfile.foto,
          tema_cor: defaultProfile.tema_cor
        };

        const response = await authService.updateUserProfile(updateData);

        updateUserData({
          ...response.user,
          foto: defaultProfile.foto
        });

        showAlert('Perfil restaurado para o padrão com sucesso!', 'success');
      } catch (error) {
        setPerfil({
          nome: usuario?.nome || '',
          descricao: usuario?.descricao || '',
          foto: usuario?.foto || null,
          tema_cor: usuario?.tema_cor || '#ef4444'
        });
        showAlert('Erro ao restaurar perfil: ' + error.message, 'error');
      } finally {
        setLoading(false);
      }
    }
  };

  const handleRemovePhoto = async () => {
    if (window.confirm('Tem certeza que deseja remover sua foto de perfil?')) {
      try {
        setLoading(true);
        const updateData = { ...perfil, foto: null };
        const response = await authService.updateUserProfile(updateData);

        updateUserData({
          ...response.user,
          foto: null
        });

        setPerfil(prev => ({ ...prev, foto: null }));
        showAlert('Foto de perfil removida com sucesso!', 'success');
      } catch (error) {
        showAlert(error.message, 'error');
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-darker rounded-lg p-6">
        <h2 className="text-2xl font-bold mb-6">Informações do Perfil</h2>
        <div className="space-y-4">
          {/* Seção da Foto */}
          <div className="flex justify-center mb-6">
            <div className="relative">
              <img
                src={perfil.foto || getDefaultAvatar(perfil.nome)}
                alt="Foto de perfil"
                className="w-32 h-32 rounded-full object-cover border-4 border-primary"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = FALLBACK_AVATAR;
                }}
              />
              <div className="absolute -bottom-2 right-0 flex gap-2">
                <label
                  htmlFor="foto"
                  className="bg-primary text-white p-2 rounded-full cursor-pointer hover:bg-hover"
                  title="Alterar foto"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
                  </svg>
                </label>
                {perfil.foto && (
                  <button
                    onClick={handleRemovePhoto}
                    className="bg-red-600 text-white p-2 rounded-full hover:bg-red-700 transition-colors"
                    title="Remover foto"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                    </svg>
                  </button>
                )}
              </div>
              <input
                type="file"
                id="foto"
                accept="image/*"
                className="hidden"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onloadend = () => {
                      setPerfil(prev => ({ ...prev, foto: reader.result }));
                    };
                    reader.readAsDataURL(file);
                  }
                }}
              />
            </div>
          </div>

          {/* Campos do Formulário */}
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Nome</label>
              <input
                type="text"
                className="w-full p-3 rounded bg-dark text-light"
                value={perfil.nome}
                onChange={(e) => setPerfil({ ...perfil, nome: e.target.value })}
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Descrição</label>
              <textarea
                className="w-full p-3 rounded bg-dark text-light h-32"
                value={perfil.descricao}
                placeholder="Conte um pouco sobre você..."
                onChange={(e) => setPerfil({ ...perfil, descricao: e.target.value })}
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">Cor do Tema</label>
              <input
                type="color"
                className="w-full h-12 p-1 rounded bg-dark cursor-pointer"
                value={perfil.tema_cor}
                onChange={(e) => setPerfil({ ...perfil, tema_cor: e.target.value })}
              />
            </div>
          </div>

          {/* Botões */}
          <div className="flex gap-4">
            <button
              className="flex-1 py-3 bg-primary text-white rounded hover:bg-hover"
              onClick={handleSave}
              disabled={loading}
            >
              {loading ? 'Salvando...' : 'Salvar Alterações'}
            </button>

            <button
              className="py-3 px-6 bg-gray-700 text-white rounded hover:bg-gray-600 transition-colors"
              onClick={handleReset}
              title="Restaurar perfil para o padrão"
              disabled={loading}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
