import { Link } from 'react-router-dom';

export function Card({ item, onFavorite, isAuthenticated }) {
  return (
    <Link 
      to={`/detalhes/${item.collection}/${item._id}`}
      className="card-container group"
    >
      <div className="aspect-[2/3] relative overflow-hidden rounded-lg shadow-xl">
        <img 
          src={item.img_url} 
          alt={item.titulo}
          className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent 
                        opacity-0 group-hover:opacity-100 transition-all duration-300
                        flex flex-col justify-end p-4">
          <h3 className="text-lg font-bold text-white transform translate-y-4 group-hover:translate-y-0 
                         transition-transform duration-300">
            {item.titulo}
          </h3>
          <div className="flex items-center gap-3 mt-4 transform translate-y-8 group-hover:translate-y-0 
                          transition-transform duration-300 delay-100">
            <button 
              onClick={(e) => {
                e.preventDefault();
                window.location.href = item.url;
              }}
              className="watch-button"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                      d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"/>
              </svg>
              <span>Assistir</span>
            </button>
            {isAuthenticated && (
              <button 
                onClick={(e) => {
                  e.preventDefault();
                  onFavorite?.(item);
                }}
                className="favorite-button"
              >
                <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/>
                </svg>
              </button>
            )}
          </div>
        </div>
      </div>
    </Link>
  );
}
