import { useState, useEffect, useContext } from 'react';
import { authService, amigoService } from '../services/api';
import { AuthContext } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { Alert } from '../components/Alert';
import imageCompression from 'browser-image-compression'; // Adicione esta importação
import { Footer } from '../components/Footer';

export function Perfil() {
  const [userData, setUserData] = useState({ nome: '', email: '', descricao: '', foto: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [previewFoto, setPreviewFoto] = useState('');
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const { updateUserData } = useContext(AuthContext);
  const { themeColor, setThemeColor } = useTheme();
  const [showAlert, setShowAlert] = useState(null);
  const [amigos, setAmigos] = useState([]);
  const [solicitacoes, setSolicitacoes] = useState([]);
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [emailAmigo, setEmailAmigo] = useState('');

  useEffect(() => {
    async function fetchUserData() {
      try {
        setLoading(true);
        const data = await authService.getUserProfile();
        setUserData(data);
        setPreviewFoto(data.foto);
      } catch (err) {
        setError('Erro ao carregar dados do perfil.');
      } finally {
        setLoading(false);
      }
    }
    fetchUserData();
    carregarAmigos();
    carregarSolicitacoes();
  }, []);

  const carregarAmigos = async () => {
    const data = await amigoService.listarAmigos();
    setAmigos(data);
  };

  const carregarSolicitacoes = async () => {
    const data = await amigoService.listarSolicitacoes();
    setSolicitacoes(data);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const compressImage = async (file) => {
    const options = {
      maxSizeMB: 1,
      maxWidthOrHeight: 800,
      useWebWorker: true,
      fileType: 'image/jpeg', // Força conversão para JPEG
      initialQuality: 0.7, // Reduz a qualidade para diminuir tamanho
    };

    try {
      return await imageCompression(file, options);
    } catch (error) {
      console.error('Erro ao comprimir imagem:', error);
      throw new Error('Não foi possível comprimir a imagem. Tente outra imagem.');
    }
  };

  const handleFotoChange = async (e) => {
    const file = e.target.files[0];
    
    // Validar tamanho do arquivo (2MB = 2 * 1024 * 1024 bytes)
    const MAX_FILE_SIZE = 2 * 1024 * 1024; 
    
    if (file) {
      if (file.size > MAX_FILE_SIZE) {
        showAlertMessage('A imagem selecionada é muito grande. Por favor, escolha uma imagem de até 2MB.', 'error');
        e.target.value = ''; // Limpa o input
        return;
      }

      try {
        setLoading(true);
        const compressedFile = await compressImage(file);
        
        // Verificar novamente após compressão
        if (compressedFile.size > MAX_FILE_SIZE) {
          throw new Error('Mesmo após a compressão, a imagem ainda é muito grande. Por favor, escolha outra imagem.');
        }
        
        const reader = new FileReader();
        reader.onload = () => {
          setPreviewFoto(reader.result);
          setUserData(prev => ({ ...prev, foto: reader.result }));
        };
        reader.readAsDataURL(compressedFile);
      } catch (err) {
        console.error('Erro no processamento da imagem:', err);
        showAlertMessage(
          err.message || 'Erro ao processar imagem. Tente uma imagem menor ou em outro formato.',
          'error'
        );
        e.target.value = ''; // Limpa o input
      } finally {
        setLoading(false);
      }
    }
  };

  const handleColorChange = (e) => {
    const newColor = e.target.value;
    setThemeColor(newColor);
    document.documentElement.style.setProperty('--color-primary', newColor);
    // Garantir que a cor do hover seja 20% mais escura usando color-mix
    document.documentElement.style.setProperty('--color-hover', `color-mix(in srgb, ${newColor} 80%, black)`);
    setUserData(prev => ({ ...prev, tema_cor: newColor }));
  };

  const showAlertMessage = (message, type) => {
    setShowAlert({ message, type });
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      const response = await authService.updateUserProfile({
        ...userData,
        tema_cor: themeColor
      });
      
      updateUserData({
        nome: userData.nome,
        foto: userData.foto,
        descricao: userData.descricao,
        tema_cor: themeColor
      });

      showAlertMessage('Perfil atualizado com sucesso!', 'success');
    } catch (err) {
      showAlertMessage(err.response?.data?.message || 'Erro ao atualizar perfil.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleEnviarSolicitacao = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      await amigoService.enviarSolicitacao(emailAmigo);
      setShowAddFriend(false);
      setEmailAmigo('');
      showAlertMessage('Solicitação enviada com sucesso!', 'success');
    } catch (error) {
      setError(
        error.message === 'Usuário não encontrado' 
          ? 'Não encontramos nenhum usuário com este email' 
          : error.message
      );
    } finally {
      setLoading(false);
    }
  };

  const handleResponderSolicitacao = async (solicitacaoId, aceitar) => {
    try {
      await amigoService.responderSolicitacao(solicitacaoId, aceitar);
      await carregarSolicitacoes();
      await carregarAmigos();
      showAlertMessage(
        aceitar ? 'Amigo adicionado com sucesso!' : 'Solicitação rejeitada',
        aceitar ? 'success' : 'info'
      );
    } catch (error) {
      showAlertMessage(error.message, 'error');
    }
  };

  const PasswordChangeModal = () => {
    const [passwords, setPasswords] = useState({ current: '', new: '', confirm: '' });
    const [modalError, setModalError] = useState('');
    const [modalLoading, setModalLoading] = useState(false);

    const handlePasswordChange = async (e) => {
      e.preventDefault();
      if (passwords.new !== passwords.confirm) {
        setModalError('As senhas não coincidem');
        return;
      }

      setModalLoading(true);
      try {
        await authService.changePassword(passwords.current, passwords.new);
        setShowPasswordModal(false);
        showAlertMessage('Senha alterada com sucesso!', 'success');
      } catch (err) {
        setModalError(err.response?.data?.message || 'Erro ao alterar senha');
      } finally {
        setModalLoading(false);
      }
    };

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-dark p-6 rounded-lg w-96">
          <h3 className="text-xl font-bold mb-4 text-light">Alterar Senha</h3>
          {modalError && (
            <div className="bg-red-500 text-white p-2 rounded mb-4">
              {modalError}
            </div>
          )}
          <form onSubmit={handlePasswordChange} className="space-y-4">
            <input
              type="password"
              placeholder="Senha Atual"
              value={passwords.current}
              onChange={e => setPasswords({...passwords, current: e.target.value})}
              className="w-full p-2 rounded bg-darker text-light"
              required
            />
            <input
              type="password"
              placeholder="Nova Senha"
              value={passwords.new}
              onChange={e => setPasswords({...passwords, new: e.target.value})}
              className="w-full p-2 rounded bg-darker text-light"
              required
              minLength={6}
            />
            <input
              type="password"
              placeholder="Confirmar Nova Senha"
              value={passwords.confirm}
              onChange={e => setPasswords({...passwords, confirm: e.target.value})}
              className="w-full p-2 rounded bg-darker text-light"
              required
              minLength={6}
            />
            <div className="flex justify-end space-x-2">
              <button
                type="button"
                onClick={() => setShowPasswordModal(false)}
                className="px-4 py-2 rounded bg-gray-600 text-white hover:bg-gray-700"
              >
                Cancelar
              </button>
              <button
                type="submit"
                disabled={modalLoading}
                className="px-4 py-2 rounded bg-primary text-white hover:bg-hover disabled:opacity-50"
              >
                {modalLoading ? 'Alterando...' : 'Alterar Senha'}
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  if (loading) return <p>Carregando...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="flex flex-col min-h-screen">
      <div className="content-section pt-20 animate-fadeIn flex-grow">
        <div className="main-content">
          <div className="container px-4 pt-24 pb-12">
            {showAlert && (
              <Alert 
                message={showAlert.message} 
                type={showAlert.type}
                onClose={() => setShowAlert(null)}
              />
            )}

            <div className="max-w-4xl mx-auto bg-darker rounded-2xl p-8 shadow-xl">
              <div className="flex flex-col items-center mb-8">
                <div className="relative group">
                  <div className="w-32 h-32 md:w-40 md:h-40 rounded-full overflow-hidden border-4 profile-avatar-border transition-colors">
                    <img
                      src={previewFoto || '/images/default-avatar.png'}
                      alt="Foto de Perfil"
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/default-avatar.png';
                      }}
                    />
                  </div>
                  <label
                    htmlFor="foto"
                    className="absolute bottom-2 right-2 nav-button bg-primary text-white p-2 rounded-full cursor-pointer hover:bg-hover transition-colors"
                    title="Alterar foto de perfil"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
                    </svg>
                  </label>
                  <input
                    type="file"
                    id="foto"
                    accept="image/*"
                    className="hidden"
                    onChange={handleFotoChange}
                  />
                </div>
                <p className="text-sm text-gray-400 mt-2">
                  JPG ou PNG, máximo 2MB. Imagens muito grandes serão redimensionadas.
                </p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-6">
                  <div>
                    <label className="profile-label block mb-2 text-sm font-medium">Nome</label>
                    <input
                      type="text"
                      name="nome"
                      value={userData.nome}
                      onChange={handleInputChange}
                      className="profile-input w-full px-4 py-3 rounded-lg text-base"
                      placeholder="Seu nome"
                    />
                  </div>

                  <div>
                    <label className="profile-label block mb-2 text-sm font-medium">Email</label>
                    <input
                      type="email"
                      value={userData.email}
                      disabled
                      className="profile-input w-full px-4 py-3 rounded-lg text-base bg-opacity-50 cursor-not-allowed"
                    />
                  </div>
                </div>

                <div className="space-y-6">
                  <div>
                    <label className="profile-label block mb-2 text-sm font-medium">Descrição</label>
                    <textarea
                      name="descricao"
                      value={userData.descricao}
                      onChange={handleInputChange}
                      className="profile-input w-full px-4 py-3 rounded-lg text-base min-h-[120px]"
                      placeholder="Conte um pouco sobre você..."
                    />
                  </div>

                  <div>
                    <label className="profile-label block mb-2 text-sm font-medium">Cor do Tema</label>
                    <div className="flex items-center space-x-4">
                      <input
                        type="color"
                        value={themeColor}
                        onChange={handleColorChange}
                        className="w-16 h-10 rounded cursor-pointer"
                      />
                      <span className="profile-text text-sm">
                        Escolha a cor do seu tema
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mt-8 pt-6 border-t border-gray-700">
                <button
                  type="button"
                  onClick={() => setShowPasswordModal(true)}
                  className="px-6 py-2.5 rounded-lg bg-gray-700 text-white hover:bg-gray-600 transition-colors w-full sm:w-auto"
                >
                  Alterar Senha
                </button>
                <button
                  onClick={handleSave}
                  disabled={loading}
                  className="px-6 py-2.5 rounded-lg bg-primary text-white hover:bg-hover transition-colors w-full sm:w-auto disabled:opacity-50"
                >
                  {loading ? 'Salvando...' : 'Salvar Alterações'}
                </button>
              </div>
            </div>

            {showPasswordModal && <PasswordChangeModal />}

            <section className="mt-8">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">Amigos</h2>
                <button
                  onClick={() => setShowAddFriend(true)}
                  className="bg-primary text-white px-4 py-2 rounded hover:bg-hover"
                >
                  Adicionar Amigo
                </button>
              </div>

              {/* Modal Adicionar Amigo */}
              {showAddFriend && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                  <div className="bg-dark p-6 rounded-lg w-96">
                    <div className="flex justify-between items-center mb-4">
                      <h3 className="text-xl font-bold">Adicionar Amigo</h3>
                      <button 
                        onClick={() => setShowAddFriend(false)}
                        className="text-gray-400 hover:text-white"
                      >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                    <form onSubmit={handleEnviarSolicitacao} className="space-y-4">
                      <div>
                        <input
                          type="email"
                          value={emailAmigo}
                          onChange={(e) => setEmailAmigo(e.target.value)}
                          placeholder="Email do amigo"
                          className="w-full p-3 rounded bg-darker text-light border border-gray-700 focus:border-primary focus:ring-1 focus:ring-primary outline-none transition-colors"
                        />
                        <p className="text-sm text-gray-400 mt-1">
                          Digite o email do usuário que deseja adicionar
                        </p>
                      </div>

                      {error && (
                        <div className="bg-red-500 bg-opacity-10 border border-red-500 text-red-500 px-4 py-3 rounded relative" role="alert">
                          <div className="flex items-center">
                            <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zm-1 9a1 1 0 01-1-1V8a1 1 0 112 0v6a1 1 0 01-1 1z" clipRule="evenodd" />
                            </svg>
                            <span>{error}</span>
                          </div>
                        </div>
                      )}

                      <div className="flex justify-end space-x-2">
                        <button
                          type="button"
                          onClick={() => setShowAddFriend(false)}
                          className="px-4 py-2 rounded bg-gray-600 text-white hover:bg-gray-700 transition-colors"
                        >
                          Cancelar
                        </button>
                        <button
                          type="submit"
                          className="px-4 py-2 rounded bg-primary text-white hover:bg-hover transition-colors flex items-center space-x-2"
                          disabled={loading}
                        >
                          {loading ? (
                            <>
                              <svg className="animate-spin h-5 w-5 mr-2" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                              </svg>
                              <span>Enviando...</span>
                            </>
                          ) : (
                            <span>Enviar Solicitação</span>
                          )}
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              )}

              {/* Lista de Solicitações Pendentes */}
              {solicitacoes?.length > 0 && (
                <div className="mb-6">
                  <h3 className="text-xl font-semibold mb-4">Solicitações Pendentes</h3>
                  <div className="space-y-4">
                    {solicitacoes.map(sol => (
                      <div key={sol?._id || 'temp-key'} className="flex items-center justify-between bg-darker p-4 rounded">
                        <div className="flex items-center space-x-4">
                          <img
                            src={sol?.de?.foto || '/images/default-avatar.png'}
                            alt={sol?.de?.nome || 'Usuário'}
                            className="w-10 h-10 rounded-full"
                            onError={(e) => {
                              e.target.onerror = null;
                              e.target.src = '/images/default-avatar.png';
                            }}
                          />
                          <span>{sol?.de?.nome || 'Usuário'}</span>
                        </div>
                        <div className="flex space-x-2">
                          <button
                            onClick={() => handleResponderSolicitacao(sol._id, true)}
                            className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                          >
                            Aceitar
                          </button>
                          <button
                            onClick={() => handleResponderSolicitacao(sol._id, false)}
                            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                          >
                            Rejeitar
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Lista de Amigos */}
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
                {amigos?.map(amigo => (
                  <div key={amigo?.usuario?._id || 'temp-key'} className="bg-darker p-4 rounded">
                    <img
                      src={amigo?.usuario?.foto || '/images/default-avatar.png'}
                      alt={amigo?.usuario?.nome || 'Usuário'}
                      className="w-20 h-20 rounded-full mx-auto mb-2"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/images/default-avatar.png';
                      }}
                    />
                    <h4 className="text-center font-medium">{amigo?.usuario?.nome || 'Usuário'}</h4>
                  </div>
                ))}
                {amigos?.length === 0 && (
                  <div className="col-span-full text-center text-gray-400 py-8">
                    Você ainda não tem amigos adicionados
                  </div>
                )}
              </div>
            </section>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
