import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import '../styles/card.css';

export function Card({ item }) {
  const navigate = useNavigate();
  const { themeColor } = useTheme();

  const handleClick = () => {
    navigate(`/detalhes/videos/${item._id}`);
  };

  // Função para escurecer a cor do tema para o hover
  const getDarkerColor = (color) => {
    const hex = color.replace('#', '');
    const rgb = parseInt(hex, 16);
    const r = (rgb >> 16) & 0xff;
    const g = (rgb >> 8) & 0xff;
    const b = rgb & 0xff;
    
    const darker = [r * 0.8, g * 0.8, b * 0.8]
      .map(c => Math.floor(c))
      .map(c => c.toString(16).padStart(2, '0'))
      .join('');
    
    return `#${darker}`;
  };

  const buttonStyle = {
    '--theme-color': themeColor,
    '--theme-color-darker': getDarkerColor(themeColor)
  };

  return (
    <div 
      className="card-container"
      onClick={handleClick}
      style={buttonStyle}
    >
      <img 
        src={item.img_url || item.img_capa_url || '/images/placeholder.jpg'} 
        alt={item.titulo}
        className="w-full h-full object-cover"
      />
      
      <div className="card-overlay">
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          <div className="watch-button flex items-center gap-2">
            <span>Assistir</span>
            <svg 
              className="w-4 h-4" 
              fill="none" 
              stroke="currentColor" 
              viewBox="0 0 24 24"
            >
              <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth={2} 
                d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"
              />
            </svg>
          </div>
        </div>
        <div className="card-title">
          <h3 className="text-sm font-semibold line-clamp-2">{item.titulo}</h3>
        </div>
      </div>
    </div>
  );
}
