import { createContext, useReducer, useEffect } from 'react';

const initialState = {
  isAuthenticated: false,
  user: null,
  token: null,
  loading: true
};

function authReducer(state, action) {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        loading: false
      };
    case 'LOGOUT':
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false
      };
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'UPDATE_USER':
      return {
        ...state,
        user: { ...state.user, ...action.payload.user }
      };
    default:
      return state;
  }
}

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [state, dispatch] = useReducer(authReducer, initialState);

  useEffect(() => {
    // Verificar autenticação existente
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (token && savedUser) {
      try {
        const user = JSON.parse(savedUser);
        dispatch({ type: 'LOGIN', payload: { user, token } });
      } catch (error) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }

    // Processar parâmetros de autenticação
    const params = new URLSearchParams(window.location.search);
    const authToken = params.get('token');
    const authUser = params.get('user');
    
    if (authToken && authUser) {
      try {
        const userData = JSON.parse(decodeURIComponent(authUser));
        localStorage.setItem('token', authToken);
        localStorage.setItem('user', JSON.stringify(userData));
        dispatch({ type: 'LOGIN', payload: { user: userData, token: authToken } });
        
        // Limpar URL
        window.history.replaceState({}, document.title, window.location.pathname);
      } catch (error) {
        console.error('Erro ao processar autenticação:', error);
      }
    }
  }, []);

  const updateUserData = (userData) => {
    dispatch({ 
      type: 'UPDATE_USER', 
      payload: { 
        user: { ...state.user, ...userData },
        token: state.token 
      } 
    });
    // Atualizar também no localStorage
    const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
    localStorage.setItem('user', JSON.stringify({ ...currentUser, ...userData }));
  };

  return (
    <AuthContext.Provider value={{ 
      ...state, 
      dispatch,
      updateUserData,
      isAuthenticated: !!state.token 
    }}>
      {children}
    </AuthContext.Provider>
  );
}
