import { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { contentService, authService } from '../services/api';
import { Carousel } from '../components/Carousel';

export function Detalhes() {
  const { collection, id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState([]);
  const [toast, setToast] = useState({ show: false, message: '', type: '' });

  useEffect(() => {
    const loadContent = async () => {
      try {
        setLoading(true);
        setError(null);
        const [itemData, recommendationsData] = await Promise.all([
          contentService.getItem(collection, id),
          contentService.getRecommendations(collection)
        ]);

        setItem(itemData);
        setRecommendations(recommendationsData.filter(rec => rec._id !== id).slice(0, 6));
      } catch (error) {
        console.error('Erro ao carregar conteúdo:', error);
        setError('Erro ao carregar conteúdo. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    loadContent();
  }, [id, collection]);

  const handleFavorite = async () => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      const tipo = collection === 'animes' ? 'anime' : 'filme';
      const response = await authService.toggleFavorite(id, tipo);
      
      setItem(prev => ({
        ...prev,
        isFavorited: response.isFavorited
      }));
      
      // Mostrar toast
      const action = response.isFavorited ? 'adicionado aos' : 'removido dos';
      setToast({
        show: true,
        message: `${item.titulo} foi ${action} favoritos!`,
        type: response.isFavorited ? 'success' : 'info'
      });

      // Esconder toast após 3 segundos
      setTimeout(() => setToast({ show: false, message: '', type: '' }), 3000);
    } catch (error) {
      setToast({
        show: true,
        message: 'Erro ao atualizar favoritos',
        type: 'error'
      });
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-darker animate-fadeIn">
      <div className="content-section pt-20">
        {error ? (
          <div className="text-center text-red-500 p-4">{error}</div>
        ) : item ? (
          <>
            <div className="flex flex-col md:flex-row gap-8">
              <div className="w-full md:w-1/3 netflix-card">
                <img 
                  src={item.img_url} 
                  alt={item.titulo}
                  className="w-full rounded-lg shadow-2xl"
                />
              </div>
              
              <div className="flex-1">
                <h1 className="text-4xl font-bold text-primary mb-4">{item.titulo}</h1>
                {item.categoria && (
                  <span className="bg-primary text-white px-4 py-2 rounded-full text-sm font-bold mb-4 inline-block">
                    {item.categoria}
                  </span>
                )}
                {item.descricao && (
                  <p className="text-light text-lg mb-8 mt-4">{item.descricao}</p>
                )}
                <div className="flex gap-4">
                  <a
                    href={item.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-primary text-white px-6 py-3 rounded-lg hover:bg-hover transition-all inline-flex items-center gap-2"
                  >
                    <span>Assistir Agora</span>
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"/>
                    </svg>
                  </a>
                  {isAuthenticated && (
                    <button
                      onClick={handleFavorite}
                      className="px-6 py-3 rounded-lg border-2 border-primary text-primary hover:bg-primary hover:text-white transition-all"
                    >
                      {item.isFavorited ? 'Remover dos Favoritos' : 'Adicionar aos Favoritos'}
                    </button>
                  )}
                </div>
              </div>
            </div>

            {recommendations.length > 0 && (
              <div className="mt-12">
                <Carousel items={recommendations} title="Recomendações" />
              </div>
            )}
          </>
        ) : null}
      </div>
    </div>
  );
}
